function sliderFunc() {
        var sliderHeight = $('.flexslider').outerHeight();
        var innerHeight = $('.inner-slide').outerHeight();
        var capBottom = innerHeight - sliderHeight + 15;
            
        if( $('.flex-direction-nav').css('display') == 'block' ){ 
            if (capBottom >= 0){
                $('.caption').css('bottom', capBottom + 'px');
            } else {
                $('.caption').css('bottom', '10px');
            }
        } else {
                $('.caption').css('bottom', '0');
        }
}

