function accordionThis(){       
    if ($(window).width() < 950){
        $("#quicklinks .ql-wrapper").addClass("accordion-this");
        $("#quicklinks ul").slideUp('fast');
      }
    else {
      $("#quicklinks .ql-wrapper").removeClass("accordion-this");
        $("#quicklinks ul").slideDown('fast');
      }
}

$(document).ready(function() {
    $('#quicklinks .sec-header').click(function(e) {
      e.preventDefault();
       
      if($(this).parent().hasClass('accordion-this')) {
          $('#quicklinks ul').slideUp();
    
          if($(this).next().is(':hidden')) {
          $(this).next().slideDown(); 
        } else {
            $(this).next().slideUp();
                }
      
    }
   });
});

