    function a11yClick(event) {
        if(event.type === 'click'){
            return true;
        }
        else if(event.type === 'keypress'){
            var code = event.charCode || event.keyCode;
            if((code === 32)|| (code === 13)){
                return true;
            }
        }
        else{
            return false;
        }
    }

	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":
				appView();
			break;
			case "320":
				appView();
			break;
		}
	}

