    function csRsMenu() {
    	// BUILD POPULAR LINKS
        var globalIcons = [
            ["Contact Us", "https://www.u-46.org/contact", "_self"],
            ["Lunch Menu", "https://www.u-46.org/Domain/6445", "_self"],
            ["Infinite Campus ", "https://campus.u-46.org/campus/portal/u46.jsp", "_self"],
            ["Careers", "https://www.u-46.org/Page/9253", "_self"],
            ["Staff Intranet", "https://www.u-46.org/Domain/10261", "_self"]
        ];
        var iconNum = ("5" == "5;4;3;2;1;0") ? 0 : "5";
        var globalLinksHeader = "", globalLinks = "";

        if(iconNum > 0) {
            globalLinksHeader = "Popular Links";
        }

        for(var i = 0; i < iconNum; i++) {
            var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
            globalLinks += '1,' + globalIcons[i][0] + ',' + linkCheck + ',' + globalIcons[i][2] + ';';
        }

    	// RESPONSIVE MENU
		$("body").csRsMenu({
			"breakPoint"			: 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
            "slideDirection"        : "right-to-left", // OPTIONS - left-to-right, right-to-left
			"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
