function AjaxSearchcse_search_box_1(){
    $[ "ui" ][ "autocomplete" ].prototype["_renderItem"] = function( ul, item) {
        return $( "
" )
            .data( "item.autocomplete", item )
            .append( $( "" ).html( item.label ) )
            .appendTo( ul );
    };
    $("#cse-search-box-1 .search-input").autocomplete({
        source: function (request, response) {
            var search_string = ($("#search_bar_moblie").is(":visible")) ? $("#cse-search-box-1 .search-input").eq(1).val() : $("#cse-search-box-1 .search-input").val();
            $.ajax({
                url: "//dynamic.schoolreviewservices.com/search/psr/"+search_string,
                //data: { search_string: $("#cse-search-box-1 .search-input").val()},
                type: "GET",
                dataType: "json",
                success: function (data) {
                    if (data) {
                        response(
                            $.map(data, function (item) {
                                return{
                                    label: item.label,
                                    link: item.link,
                                    value: item.text
                                }
                            })
                        );
                    } else {
                        response(false, false);
                    }
                },
                error: function () {
                    response(false, false);
                }
            })
        },
        select: function (event, ui) {
            window.location=ui.item.link;
            return false;
        },
        change: function (event, ui) {
            if(ui.item){
                $("#cse-search-box-1 .search-input").val(ui.item.value);
                window.location=ui.item.link;
            }
        },
        open: function(){
            $(".ui-menu").width("auto");
        }
    }).data( "autocomplete" )._renderItem = function( ul, item ) {
        return $( "
" )
            .data( "item.autocomplete", item )
            .append( ""+ item.label + "" )
            .appendTo( ul );
    };
}


