	// construct an instance of Headroom, passing the element
	headroom  = new Headroom(myElement);
	// initialise
	headroom.init();

	var myElement1 = document.querySelector(".headroom1");
	// construct an instance of Headroom, passing the element
	headroom1  = new Headroom(myElement1);
	// initialise
	headroom1.init();
}

function ShowMobileNavBar(){
    m_bar = $("#m-nav-bar");
    $("body").prepend(m_bar);
    $("#m_bar-content").empty();
    $("#m-nav-bar").width("0px");
    $("#m-header-navbar").toggle(
        function(){
			headroom.offset=headroom1.offset=999999999;
            container_width = $("#page-container").width();
            //$("body").width($("#page-container").width() + 280);
			$("#m-nav-bar").css({"width":"280px"});
            //$("#page-container").width(container_width).css("margin-left","280px");
            submenu_width=$(".cate_list").width()-($(".cate_list a").outerWidth()+6)+"px";
            $(".cate_list .cate_arr").css({"width":submenu_width});
			$("html,body").addClass("ovfHiden");
        },
        function(){
			headroom.offset=headroom1.offset=400;
			$("#m-nav-bar").width("0px");
            $("body").width("100%");
            //$("#page-container").width("100%").css("margin-left","0"); 
			window.scrollTo(0,0);
			$("html,body").removeClass("ovfHiden");
        }
    );
    $(".cate_list").toggle(
        function(){
            $(this).find(".bar-lay-2").show();
            $(this).find(".cate_arr").css("background-color","#f4b34f");
            $(this).find(".cate-span").css("background","url(data:image/png;base64,) no-repeat");
        },
        function(){
            $(this).find(".bar-lay-2").hide();
            $(this).find(".cate_arr").css("background-color","");
            $(this).find(".cate-span").css("background","url(data:image/png;base64,) no-repeat");
        }
    )
    $(".bar-lay-2").find("li").click(function(event){event.stopPropagation()});
}

function ShowLoginLine(){
    var a='MY ACCOUNTLOGOUT';
    var b='LOGINSIGN UP';
    $.ajax(
        {
            url:"/include/srn_lib/psr/html_fragments/check_login.php",
            dataType:"json",
            cache:false,
            dataType:"json",
            success:function(c){
                if(c.logged_in===true){
                    $("#header .search_bar").addClass("logged_in");
                    if($("#login_line").html()!==a){
                        $("#login_line").html(a)
                    }
                    if($("#m-login").html()!==a){
                        $("#m-login").html(a)
                    }
                }else{
                    $("#header .search_bar").removeClass("logged_in");
                    if($("#login_line").html()!==b){
                        $("#login_line").html(b)
                    }
                    if($("#m-login").html()!==b){
                        $("#m-login").html(b)
                    }
                }
            }
        }
    );
}

function checkIfOverLapped(el1, el2) {
    var rect1 = el1.getBoundingClientRect();
    var rect2 = el2.getBoundingClientRect();

    return !(
      rect1.top > rect2.bottom ||
      rect1.right < rect2.left ||
      rect1.bottom < rect2.top ||
      rect1.left > rect2.right
    );
}

function disableAdsClick(){
    $(".srn_ads_cnt.header-overlay").addClass("has-overlay");
}

function enableAdsClick(){
    $(".srn_ads_cnt.header-overlay").removeClass("has-overlay");
}

function HoverOnSubcategoryMenu(){
    var t4;
    $(".navigation li.dropdown4").mouseenter(function(){
        disableAdsClick();
        var p_str = $(".navigation li.dropdown4").width();
        var m_str = $("#down-list4").width();
        var off_set = $(".navigation li.dropdown4").offset().left - m_str + p_str - 18;
        t4 = setTimeout(function(){
            $("#down-list4").show().offset({left:off_set});
            $(".navigation li.dropdown4").addClass("navigation_hover");
        },300)
    })
    $("#down-list4").mouseenter(function(){
        disableAdsClick();
        var p_str = $(".navigation li.dropdown4").width();
        var m_str = $("#down-list4").width();
        var off_set = $(".navigation li.dropdown4").offset().left - m_str + p_str - 18;
        $("#down-list4").show().offset({left:off_set});
        $(".navigation li.dropdown4").addClass("navigation_hover");
    })
    $(".navigation li.dropdown4, #down-list4").mouseleave(function(e){
        if(!$(e.relatedTarget).hasClass("has-overlay")){
           enableAdsClick(); 
        }else{
            $(e.relatedTarget).mouseleave(function(){
                enableAdsClick(); 
            });
        }
        if (typeof t4 !== "undefined") clearTimeout(t4);
        $("#down-list4").hide();
        $(".navigation li.dropdown4").removeClass("navigation_hover");
    });
    $(".category-tab a.menu_link").mouseover(function(){
        disableAdsClick();
        var index = $(this).index();
        $(".category-content .subcategory_inner_div").hide();
        $(".category-content .subcategory_inner_div:eq("+index+")").show();
    });   
}

