var listDoms=[];
var originDoms=[];
var clickCount=[];
function sortDataListDoms(row_class,column_class,n, orderby) {
    var row_length=$(row_class.toString()).length;
    key="class-"+row_class.toString()+"-row-length-"+row_length.toString()+"-data-column-"+n.toString();
    temp=storeCurrentlistDoms(row_class);
    if(n==0 && orderby=="asc"){
        return temp;
    }else{
        if(orderby=="asc"){
            temp.sort(function($a,$b){
                tmp_data_a=$a.children(column_class).eq(n).data("for-compare");
                tmp_data_b=$b.children(column_class).eq(n).data("for-compare");
                if(tmp_data_a=="0"){
                    tmp_data_a=100000;
                }
                if(tmp_data_b=="0"){
                    tmp_data_b=100000;
                }
                return tmp_data_a > tmp_data_b?1: -1;
            });
        }else{
            temp.sort(function($a,$b){
                tmp_data_a=$a.children(column_class).eq(n).data("for-compare");
                tmp_data_b=$b.children(column_class).eq(n).data("for-compare");
                return tmp_data_a < tmp_data_b?1: -1
            });
        }
        if(typeof listDoms[orderby]==="undefined"){
            listDoms[orderby]=[];
        }
        listDoms[orderby][key]=temp.slice(0);
        return listDoms[orderby][key];
    }
}
function storeCurrentlistDoms(row_class){
    var row_length=$(row_class.toString()).length;
    var stored_key="class-"+row_class.toString()+"-row-length-"+row_length;
    if(typeof originDoms[stored_key]==="undefined"){
        originDoms[stored_key]=[];
        $(row_class.toString()).each(function(i){
            originDoms[stored_key][i] = $(this).clone();
        });
    }
    return originDoms[stored_key].slice(0);
}

function triggerListOrder(row_class,column_class,n, orderby){
    var row_length=$(row_class.toString()).length;
    key="class-"+row_class.toString()+"-row-length-"+row_length.toString()+"-data-column-"+n.toString();
    if(typeof listDoms[orderby]!=="undefined" && typeof listDoms[orderby][key]!=="undefined"){
        doms=listDoms[orderby][key];
    }else{
        doms=sortDataListDoms(row_class,column_class,n,orderby);
    }
    $(row_class.toString()).each(function(index){
        $(this).html(doms[index].html());
    });
}
function sptSortList(){
    storeCurrentlistDoms(".spt-list-row");
    $("body").on("click", ".spt-list-head-row .list_order", function() {
        n = $(this).index();
        row_class=".spt-list-row";
        column_class=".spt-list-column";
        var row_length=$(row_class.toString()).length;
        key="class-"+row_class.toString()+"-row-length-"+row_length.toString()+"-data-column-"+n.toString();
        if(typeof clickCount[n]!=="undefined" && typeof clickCount[n][key]!=="undefined"){
            clickCount[n][key]++;
        }else{
            clickCount[n]=[];
            clickCount[n][key]=0;
            clickCount[n][key]++;
        }
        if(clickCount[n][key] % 3 === 1){
            $(".list_order").removeClass("list_order_asc").removeClass("list_order_desc");
            $(this).addClass("list_order_desc");
            triggerListOrder(row_class,column_class,n, "desc");
        }else if(clickCount[n][key] % 3 === 2){
            $(".list_order").removeClass("list_order_asc").removeClass("list_order_desc");
            $(this).addClass("list_order_asc");
            triggerListOrder(row_class,column_class,n, "asc");
        }else{
            $(".list_order").removeClass("list_order_asc").removeClass("list_order_desc");
            triggerListOrder(row_class,column_class,0, "asc");
        }
   });
}
function SptRowClick(){
    $(".spt-list-row").click(function() {
        var href = $(this).find("a").attr("href");
        if(href) {
            window.location = href;
        }
    });
}

 function stickyAdsFix(){
    var stickyad=$(".srn_ads_cnt.custom-sticky-ads");
    if(stickyad.length){
        var top=stickyad.offset().top;
        var scrollT = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
        var main_height = document.getElementById("main-content").offsetHeight;
        var sec_height = document.getElementById("secondary-content").offsetHeight;
        var footerST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height();
        var stikyStatrST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height()-890;
        var stikyEndST=document.documentElement.scrollHeight-document.documentElement.clientHeight-$("#search_footer_div").height()-290;
        var topFinal=document.documentElement.clientHeight-$("#search_footer_div").height()-890;
        if((scrollT+20)<top){
            $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"static","text-align":"left"});
        }else{
            if(scrollT<stikyStatrST){
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":"20px"});
            }else if((scrollT<=stikyEndST) && (scrollT>=stikyStatrST)){
                var top_val=document.documentElement.clientHeight-620;
                if(top_val>=0){
                    top_val=20;
                }else{
                    top_val=topFinal;
                }
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }else if((scrollT<=footerST) && (scrollT>=stikyEndST)){
                var top_val=document.documentElement.clientHeight-620;
                if(top_val>=0){
                    top_val=20;
                }else{
                    top_val=topFinal;
                }
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }else{
                top_val=topFinal;
                $(".srn_ads_cnt.custom-sticky-ads > :first-child").css({"position":"fixed","top":top_val+"px"});
            }
        }
    }
} 

function SearchboxAction(){
    $(".cse-search-box button[type=submit]").removeAttr("disabled");
    $(".cse-search-box").each(function() {
        var f = $(this).get(0);
        var q = $(this).find("input[name=q]");
        var input_elements = q.get(0);
        var fomr_id = $(this).attr("id");
        var input_id = q.attr("id");
        var x_delete = $(this).find("span[class=x_delete]");
        var n = navigator;
        var l = location;
        var su = function() {
                var siteurl_input=$(this).find("input[name=siteurl]");
                var v = document.location.toString();
                var existingSiteurl = /(?:[?&]siteurl=)([^&#]*)/.exec(v);
                if (existingSiteurl) {
                    v = decodeURI(existingSiteurl[1])
                }
                var delimIndex = v.indexOf("://");
                if (delimIndex >= 0) {
                    v = v.substring(delimIndex + "://".length, v.length)
                }
                siteurl_input.val(v);
            };
        if (window.history.navigationMode) {
            window.history.navigationMode = "compatible"
        }
        var b = "#fff";
        q.css({"background":b,"outline":"none"});
        q.attr("readonly",false);
        q.focus(function() {
            if (q.val() == "Enter school name, location or keyword") {
                q.css({"color":"#a9a9a9"});
                q.val("");
            }
        });
        q.blur(function() {
            if (q.val() == "") {
                q.css({"color":"#a9a9a9"});
                q.val("Enter school name, location or keyword");
            }
        });
        q.keyup(function() {
            x_delete.fadeIn();
            if ($.trim(q.val()) == "") {
                x_delete.fadeOut();
            }
        });
        x_delete.click(function() {
            q.css({"color":"#1E8BC3"});
            q.val("Enter school name, location or keyword");
            x_delete.hide();
        });
        $(this).submit(function() {
            if (q.val() == "Enter school name, location or keyword") {
                q.val("");
            }
        });
    });
}

 window.addEventListener("load", function(){
  var iframe = document.createElement("iframe");
  iframe.height = "1px";
  iframe.width = "1px";
  iframe.id = "ads-text-iframe";
  iframe.src = "//www.publicschoolreview.com/ads.html";

  document.body.appendChild(iframe);

  setTimeout(function(){
    var iframe = document.getElementById("ads-text-iframe");
    if(iframe.style.display == "none" || iframe.style.display == "hidden" || iframe.style.visibility == "hidden" || iframe.offsetHeight == 0)
    {
        iframe.remove();

        if(typeof gtag !== 'undefined'){
            gtag('event', 'Yes', {
                'event_category': 'Adblock Impressions Lost',
                'non_interaction': true
            });
        }
    }
    else
    {
        iframe.remove();

        if(typeof gtag !== 'undefined'){
            gtag('event', 'NO', {
                'event_category': 'Adblock Impressions Lost',
                'non_interaction': true
            });
        }
    }
  }, 1000);
}, false);

