div.script:
		function scrollEventsArrowShow() {
				var containerWidth = jQuery('.view-id-calendar.view-display-id-block_2 .view-content').width();
        var maxScroll = (jQuery('.view-id-calendar.view-display-id-block_2 .view-content').width() - jQuery('.view-id-calendar.view-display-id-block_2 .view-content').scrollLeft()) - jQuery('.view-id-calendar.view-display-id-block_2 .view-content').width();
        if ((containerWidth - jQuery('.view-id-calendar.view-display-id-block_2 .view-content').scrollLeft()) <= 100) {
						jQuery('#content-top .content-top-inner #pnAdvancerRight2').css("display", "none");
						jQuery('#content-top .content-top-inner .view-id-calendar.view-display-id-block_2 .view-content').css("width", '80%');
        } else {
            jQuery('#content-top .content-top-inner #pnAdvancerRight2').css("display", "block");
						jQuery('#content-top .content-top-inner .view-id-calendar.view-display-id-block_2 .view-content').css("width", '75%');
        }
        if ( 0 == maxScroll) {
            jQuery('#content-top .content-top-inner #pnAdvancerLeft2').css("display", "none");
						jQuery('#content-top .content-top-inner .view-id-calendar.view-display-id-block_2 .view-content').css({'margin-left': '0','width':'80%'});
        } else {
            jQuery('#content-top .content-top-inner #pnAdvancerLeft2').css("display", "block");
						jQuery('#content-top .content-top-inner .view-id-calendar.view-display-id-block_2 .view-content').css('margin-left', '5%');
        }
    }
		function scrollEvents(direction) {
			if (direction=='Go_L') {
					jQuery('.view-id-calendar.view-display-id-block_2 .view-content').animate({
							scrollLeft: "-=" + 350 + "px"
					}, function(){
							// createCookie('scrollPos', $('#slide-wrap').scrollLeft());
							scrollEventsArrowShow();
					});
			}else
			if (direction=='Go_R') {
					jQuery('.view-id-calendar.view-display-id-block_2 .view-content').animate({
							scrollLeft: "+=" + 350 + "px"
					}, function(){
							scrollEventsArrowShow();
							// createCookie('scrollPos', $('#slide-wrap').scrollLeft());
					});
			}
		 }

		function scrollArrowShowDistrict() {
				var containerWidth = jQuery('.view-id-news.view-display-id-block_0 .view-content').width();
        var maxScroll = (jQuery('.view-id-news.view-display-id-block_0 .view-content').width() - jQuery('.view-id-news.view-display-id-block_0 .view-content').scrollLeft()) - jQuery('.view-id-news.view-display-id-block_0 .view-content').width();
        if ((containerWidth - jQuery('.view-id-news.view-display-id-block_0 .view-content').scrollLeft()) <= 100) {
						jQuery('#block-views-news-block-0 #pnAdvancerRight').css("display", "none");
						jQuery('.view-id-news.view-display-id-block_0 .view-content').css("width", '96%');
        } else {
            jQuery('#block-views-news-block-0 #pnAdvancerRight').css("display", "block");
						jQuery('.view-id-news.view-display-id-block_0 .view-content').css("width", '93%');
        }
        if ( 0 == maxScroll) {
            jQuery('#block-views-news-block-0 #pnAdvancerLeft').css("display", "none");
						jQuery('.view-id-news.view-display-id-block_0 .view-content').css({'margin-left': '0','width':'97%'});
        } else {
            jQuery('#block-views-news-block-0 #pnAdvancerLeft').css("display", "block");
						jQuery('.view-id-news.view-display-id-block_0 .view-content').css('margin-left', '4%');
        }
    }

		function scrollThumbDistrict(direction) {
			if (direction=='Go_L') {
					jQuery('.view-id-news.view-display-id-block_0 .view-content').animate({
							scrollLeft: "-=" + 350 + "px"
					}, function(){
							// createCookie('scrollPos', $('#slide-wrap').scrollLeft());
							scrollArrowShowDistrict();
					});
			}else
			if (direction=='Go_R') {
					jQuery('.view-id-news.view-display-id-block_0 .view-content').animate({
							scrollLeft: "+=" + 350 + "px"
					}, function(){
							// createCookie('scrollPos', $('#slide-wrap').scrollLeft());
							scrollArrowShowDistrict();
					});
			}
		 }

link.script:
	jQuery(document).ready(function() {
		if (jQuery.cookie('arps_modal_shown') == null) {
				var d = new Date();
				d.setHours(d.getHours() + 6);
				jQuery.cookie('arps_modal_shown', 'yes', { expires: d , path: '/' });
				jQuery('#myModal').modal();
		}
});
