		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker9 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker9 a.ui-datepicker-prev').click();
	    		handlePrevClick9();
	    	}
	    });
	    $('#datepicker9 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker9 a.ui-datepicker-next').click();
	    		handleNextClick9();
	    	}
	    });


		function handlePrevClick9(){
			setTimeout(function () {
				triggerClick9();
			    $('#datepicker9').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick9(){
			setTimeout(function () {
				triggerClick9();
			    $('#datepicker9').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick9(){
			prev = $('#datepicker9').find('.ui-datepicker-prev');
			next = $('#datepicker9').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick9);
			$(prev).on('click', handlePrevClick9);
		}

		};



