				"slideDirection"		: "top-to-bottom", // OTHER OPTION IS 'left-to-right'
				"menuPosition"		: "absolute", // OTHER OPTION IS 'relative'
				"menuButtonParent"	: "#gb-header",
				"showMenuBtnText"	: "no",
				"menuBtnText"		: "Menu",
				"showMenuInfoText"	: "no",
				"menuInfoText768"	: "",
				"menuInfoText640"	: "",
				"menuInfoText480"	: "",
				"menuInfoText320"	: "",
				"showSearch"		: "yes",
				"showMyAccount"		: "yes",
				"showSiteManager"       : "yes",
				"showDistrictHome"	: "yes",
				"showSchoolList"		: "yes",
				"showChannelList"	: "yes",
				"showTranslate"		: "yes",
				"translateId"           : "none",
				"extraAccountItems"	: [], // ARRAY OF BUTTON IDS OR CLASSES
				"extraMenuOptions"	: globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
				"extraMenuItems"		: globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
				"siteID"			: "4"
			});

			addFramesetTranslate();
			mystartBtns();
		});

		head.ready("rsPagelistMenu", function() {
			$("#sp-content").creativePagelistMenu({
