	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			$(window).scroll(parallaxScroll);
			photoGallery();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			$(window).scroll(parallaxScroll);
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			$(window).scroll(parallaxScroll);
			photoGallery();
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			$(window).unbind("scroll", parallaxScroll);
			parallaxScrollReset();
			photoGallery();
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView();
		}
	}

	function photoGallery(){
		head.ready("photoGallery", function(){
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
				// PHOTO GALLERY ROTATOR
				$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
					"imageWidth" 			: 960,
				        "imageHeight" 			: 400,
				        "appHeader" 			: "no",
				        "titles" 				: "yes",
				        "captions" 				: "yes",
				        "playPause" 			: "yes",
				        "mainControls" 			: "yes",
				        "links" 				: "yes",
					"contMoreLink" 			: "no",
				        "contMoreLinkText" 		: "Read More",
				        "descMoreLink" 			: "yes",
				        "descMoreLinkText" 		: "READ MORE",
				        "bullets" 				: "no",
				        "bulletControls"			: "no",
					"thumbnails" 			: "no",
					"thumbnailWidth" 		: 122,
					"thumbnailHeight" 		: 53,
					"thumbnailSlideDirection" 	: "horizontal", // horizontal or vertical
				        "hoverPause" 			: "yes",
				        "transitionType" 			: "slide",
				        "transitionSpeed" 		: 2,
				        "onImageLoad" 			: function() { },
				        "allImagesLoaded" 		: function() { },
					"onThumbnailLoad" 		: function() { },
					"allThumbnailsLoaded" 		: function() { },
				        "onTransitionStart" 		: function() {
				        		// HIDE THE READ MORE LINK IF THE NEXT IMAGE IS NOT LINKED
				        		var nextLinkCheck = $("#hp-slideshow .rs-photo-gallery-images ul.rs-photo-gallery-images-list li.next a").length;
						if(nextLinkCheck == 0) {
							$("#hp-slideshow .rs-photo-gallery-desc-more-link").fadeOut(1000);
						} else {
							$("#hp-slideshow .rs-photo-gallery-desc-more-link").delay(1000).fadeIn(1000);
						}
				        },
				        "onTransitionEnd" 		: function() { },
				        "allLoaded" 			: function() {
				        		// HIDE THE READ MORE LINK IF THE FIRST IMAGE IS NOT LINKED
				        		var nextLinkCheck = jQuery.trim($("#hp-slideshow .rs-photo-gallery-images ul.rs-photo-gallery-images-list li.active .record-link").text());
						if(nextLinkCheck == "") {
							$("#hp-slideshow .rs-photo-gallery-desc-more-link").hide();
						}

				        		// MOVE THE GALLERY CONTROLS INTO THE DESCRIPTION CONTAINER
				        		$("#hp-slideshow .rs-photo-gallery-desc").children().wrapAll('
');
				        		$("#hp-slideshow .rs-photo-gallery-desc").append('
');
				        		$("#hp-slideshow .rs-photo-gallery-controls").append($("#hp-slideshow .rs-photo-gallery-control.back"));
				        		$("#hp-slideshow .rs-photo-gallery-controls").append($("#hp-slideshow .rs-photo-gallery-control.play-pause"));
				        		$("#hp-slideshow .rs-photo-gallery-controls").append($("#hp-slideshow .rs-photo-gallery-control.next"));
				        }
				});
			}
		});
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-content div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

