                            // FOCUS PREVIOUS ITEM
                            $(this).closest(".sw-channel-dropdown").find("a").attr("tabindex", "-1");
                            $(this).closest(".cs-mega-menu-group").prev(".cs-mega-menu-group").find("a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT ARROW
                    case template.KeyCodes.right:
                    	// FOCUS CHANNEL
                        $(this).closest(".sw-channel-dropdown").find("a").attr("tabindex", "-1");
                        $(this).closest(".sw-channel-item").next(".sw-channel-item").find("> a").focus();

                		$(this).closest(".sw-channel-item").removeClass("hover");
                        $(".sw-channel-dropdown").slideUp(300, "swing");
                    break;

                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).closest(".cs-mega-menu-group").is(":last-child")) {
                            $(this).closest(".sw-channel-item").find("> a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(".sw-channel-dropdown").find("a").attr("tabindex", "-1");
                            $(this).closest(".cs-mega-menu-group").next(".cs-mega-menu-group").find("a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS CHANNEL
                            $(this).closest(".sw-channel-dropdown").find("a").attr("tabindex", "-1");
                            if($(this).closest(".sw-channel-item").prev(".sw-channel-item").length) {
                                $(this).closest(".sw-channel-item").prev(".sw-channel-item").find("> a").focus();
                                $(this).closest(".sw-channel-item").removeClass("hover");
                            } else {
                                $(this).closest(".sw-channel-item").find("> a").focus();
                            }

                            $(".sw-channel-dropdown").slideUp(300, "swing");
                        } else {
                        	e.preventDefault();

                            // FOCUS CHANNEL
                            $(this).closest(".sw-channel-dropdown").find("a").attr("tabindex", "-1");
                            $(this).closest(".sw-channel-item").next(".sw-channel-item").find("> a").focus();

                            $(".sw-channel-dropdown").slideUp(300, "swing");
                        }
                    break;
                }
            });

            $("#channel-navigation").on("keydown", "li.sw-channel-item > a", function(e) {
            	if(e.keyCode == template.KeyCodes.down && $("~ .cs-mega-menu-dropdown", this).length) {
                	$("~ .cs-mega-menu-dropdown a:first", this).focus();
                }
            });
        },

        "Body": function() {
        	var template = this;

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //DOC add signed out breadcrumb
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            // ADJUST SIGN IN PAGE
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

            // ADJUST REGISTER PAGE
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            // ADJUST SEARCH RESULTS PAGE
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span").text("View All District News");

            // ADJUST CALENDAR LINK
            $("#hp-content-two .ui-widget.app.upcomingevents a.view-calendar-link").wrap('

');
            $("#hp-content-two .ui-widget.app.upcomingevents .cs-events-toggle-container-inner").prepend('Expand Events');
            $("a.view-calendar-link span").text("View Full Calendar");

            //GENERATE ROTATOR AREA SITE SHORTCUTS TEXT AND REMOVE ALT TEXT FROM SLIDESHOW SITE SHORTCUTS IMAGES
            $("#hp-content-one .navigation li a").each(function(i) {
            	if ($(this).attr("title")) {
                var title = $(this).attr("title");
            		$(this).append("" + title + "");
                    $(this).attr("aria-label", title);
            	}
                $("img", this).attr("alt", "").wrap('');

                $(".cs-icon-wrapper", this).prepend('');
            	template.ShortcutsCanvas({"default": "cs-icon-canvas-border-" + i + "-default", "hover": "cs-icon-canvas-border-" + i + "-hover"});
            });

            // WRAP HEADLINES MORE LINK IN ITS OWN CONTAINER AND MOVE
            $(".ui-widget.app.headlines").each(function(){
            	$("a.more-link", this).appendTo($(".ui-widget-detail", this));
                    $("a.more-link", this).wrap('
');
            });

            // WRAP HEADLINE IMAGES
            $(".headlines .ui-article-thumb .img").each(function() {
            	$("img", this).wrap('');
            });
            $(".headlines .ui-article-thumb .img img:not([src])").each(function() {
            	$(this).closest(".ui-article-thumb").remove();
            });

            // UPCOMING EVENTS FOCUS
            $(".upcomingevents .ui-articles > li").focusin(function() {
            	$(this).addClass("focus");
            }).focusout(function() {
            	$(this).removeClass("focus");
            });

            $("#sp-content-top h2:empty").replaceWith('');
        },

        "ShortcutsCanvas": function(props) {
        	var canvasDefault = document.getElementById(props.default);
            var ctxDefault = canvasDefault.getContext("2d");

            ctxDefault.save();
            ctxDefault.beginPath();
            ctxDefault.moveTo(0.0, 0.0);
            ctxDefault.bezierCurveTo(41.7, 41.3, 83.3, 82.7, 125.0, 124.0);
            ctxDefault.bezierCurveTo(166.7, 82.7, 208.3, 41.3, 250.0, 0.0);
            ctxDefault.bezierCurveTo(250.0, 2.3, 250.0, 4.7, 250.0, 7.0);
            ctxDefault.bezierCurveTo(208.3, 48.3, 166.7, 89.7, 125.0, 131.0);
            ctxDefault.bezierCurveTo(83.3, 89.7, 41.7, 48.3, 0.0, 7.0);
            ctxDefault.bezierCurveTo(0.0, 6.7, 0.0, 6.3, 0.0, 6.0);
            ctxDefault.bezierCurveTo(0.0, 4.0, 0.0, 2.0, 0.0, 0.0);
            ctxDefault.closePath();
            ctxDefault.fillStyle = "#063d71"
            ctxDefault.fill();
            ctxDefault.restore();

            var canvasHover = document.getElementById(props.hover);
            var ctxHover = canvasHover.getContext("2d");

            ctxHover.save();
            ctxHover.beginPath();
            ctxHover.moveTo(0.0, 0.0);
            ctxHover.bezierCurveTo(41.7, 41.3, 83.3, 82.7, 125.0, 124.0);
            ctxHover.bezierCurveTo(166.7, 82.7, 208.3, 41.3, 250.0, 0.0);
            ctxHover.bezierCurveTo(250.0, 2.3, 250.0, 4.7, 250.0, 7.0);
            ctxHover.bezierCurveTo(208.3, 48.3, 166.7, 89.7, 125.0, 131.0);
            ctxHover.bezierCurveTo(83.3, 89.7, 41.7, 48.3, 0.0, 7.0);
            ctxHover.bezierCurveTo(0.0, 6.7, 0.0, 6.3, 0.0, 6.0);
            ctxHover.bezierCurveTo(0.0, 4.0, 0.0, 2.0, 0.0, 0.0);
            ctxHover.closePath();
            ctxHover.fillStyle = "#fecb66";
            ctxHover.fill();
            ctxHover.restore();
        },

        "HpEvents": function() {
        	var template = this;

            $("#hp-content-two .ui-widget.app.upcomingevents").each(function() {
            	if($(this).closest("div[id*='pmi-']").next("div[id*='pmi-']").length) {
                	$(this).addClass("pad-bottom");
                }

            	var thisApp = this;
                $(".ui-articles", this).removeAttr("style");
                $(".ui-articles > li", this).removeAttr("aria-hidden");
                $(".ui-articles > li a", this).removeAttr("tabindex");
                $(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                var eventsPerRow = 0;
                var eventsProps = {
                    "eventNum": $(".ui-articles > li", this).length,
                    "eventTotalHeight": $(".ui-articles", this).outerHeight(true),
                    "eventPanelHeight": $(".ui-articles > li:first-child", this).outerHeight(true) + $(".ui-articles > li:first-child .upcoming-column.left", this).outerHeight(true) + 18 + 100, // HEIGHT OF FIRST ARTICLE + HEIGHT OF MONTH/DAY BLOCK + 18px OF TOP PADDING + 12px FOR A LITTLE BOTTOM GAP AS PER DESIGN FILE - Adam changed 12 to 100 - 12.14.17 - #03014976
                    "desktop": {
                        "eventsPerRow": 4,
                        "numOfRowsToShow": 2
                    },
                    "sevenSixtyEight": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    },
                    "sixForty": {
                        "eventsPerRow": 2,
                        "numOfRowsToShow": 2
                    }
                }

                switch(template.GetBreakPoint()) {
                    case "desktop":
                        eventsPerRow = eventsProps.desktop.eventsPerRow;
                    break;
                    case "768":
                        eventsPerRow = eventsProps.sevenSixtyEight.eventsPerRow;
                    break;
                    case "640":
                        eventsPerRow = eventsProps.sixForty.eventsPerRow;
                    break;
                }

                if(eventsProps.eventNum > eventsPerRow) {
                    $(".cs-events-toggle", this).attr("aria-label", "Expand Events").text("Expand Events").addClass("collapsed").removeClass("expanded");

                    $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", this).attr("aria-hidden", "true");
                	$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", this).attr("tabindex", "-1");
                	$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", this).attr("aria-hidden", "true");

                    $(this).addClass("expandable");
                    $(".ui-articles", this).height(eventsProps.eventPanelHeight);
                } else {
                	$(".ui-articles > li", this).removeAttr("aria-hidden");
                	$(".ui-articles > li a", this).removeAttr("tabindex");
                	$(".ui-articles > li .upcoming-column.right", this).removeAttr("aria-hidden");

                    $(this).removeClass("expandable");
                    $(".ui-articles", this).css("height", "auto");
                }

                $(this).off().on("click keydown", ".cs-events-toggle", function(e) {
                    if(template.AllyClick(e)) {
                        e.preventDefault();

                        // EXPAND EVENTS
                        if($(this).hasClass("collapsed")) {
                            $(this).attr("aria-label", "Collapse Events").text("Collapse Events").addClass("expanded").removeClass("collapsed");

                            $(".ui-articles > li", thisApp).removeAttr("aria-hidden");
                			$(".ui-articles > li a", thisApp).removeAttr("tabindex");
                            $(".ui-articles > li .upcoming-column.right[aria-hidden='true']:first", thisApp).find("a:first").focus();
                			$(".ui-articles > li .upcoming-column.right", thisApp).removeAttr("aria-hidden");

                            $(".ui-articles", thisApp).animate({
                                height: eventsProps.eventTotalHeight
                            }, 500);
                        }

                        // COLLAPSE EVENTS
                        else {
                            $(this).attr("aria-label", "Expand Events").text("Expand Events").addClass("collapsed").removeClass("expanded");

                            $(".ui-articles > li:nth-child(n+" + ((eventsPerRow * 2) + 1) + ")", thisApp).attr("aria-hidden", "true");
                			$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + ") a", thisApp).attr("tabindex", "-1");
                			$(".ui-articles > li:nth-child(n+" + (eventsPerRow + 1) + "):nth-child(-n" + (eventsPerRow * 2) + ") .upcoming-column.right", thisApp).attr("aria-hidden", "true");

                            $(".ui-articles", thisApp).animate({
                                height: eventsProps.eventPanelHeight
                            }, 500);
                        }
                    }
                });
            });
        },

        "Parallax": function() {
        	var scrollPosition = $(window).scrollTop();
        	$("#gb-background-accent").css("top", -(scrollPosition * 0.2) + "px");

        	if($(".hp").length) {
            	var shortCutsOffSet = $("#hp-content-one").offset().top - (($(window).height() / 8) * 5);
            	var headlinesOffSet = $("#hp-content-three").offset().top - (($(window).height() / 8) * 5);
                var quotesOffSet = $("#hp-content-four").offset().top - (($(window).height() / 8) * 5);
                var eventsOffSet = $("#hp-content-two").offset().top - (($(window).height() / 8) * 5);

            	if($(window).scrollTop() >= shortCutsOffSet) {
					$("#hp-content-one").addClass("animate");
                }

                if($(window).scrollTop() >= headlinesOffSet) {
					$("#hp-content-three").addClass("animate");
                }

                if($(window).scrollTop() >= quotesOffSet) {
					$("#hp-content-four").addClass("animate");
                }

                if($(window).scrollTop() >= eventsOffSet) {
					$("#hp-content-two").addClass("animate");
                }
            }

            if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var gbNavOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= gbNavOffSet) {
                            if(this.ChannelBarIsSticky) {
                            	$("header").addClass("sticky-out");
                                $("header, nav").removeClass("sticky");
                                $("header, nav").removeAttr("style");

                                this.ChannelBarIsSticky = false;

                                setTimeout(function() {
                                	$("header").removeClass("sticky-out");
                                }, 500);
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("header, nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("header, nav").removeClass("sticky");
                            $("header, nav").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }
        },

