        "Search": function() {
            // FOR SCOPE
            var _this = this;

            //SEARCH TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "Search") {
                    $(this).val("");
                }
            }).blur(function() {
                if($(this).val() == "") {
                    $(this).val("Search");
                }
            });

            // SEARCH OPEN
            $(document).on("click keydown", "#gb-search-toggle", function(e) {
            	if(_this.AllyClick(e)) {
                	e.preventDefault();

                	if($("#gb-search").hasClass("open")) {
                    	$("#gb-search").removeClass("open").attr("aria-hidden", "true");
                        $(this).attr("aria-expanded", "false");
                    } else {
                    	$("#gb-search").addClass("open").attr("aria-hidden", "false");
                    	$(this).attr("aria-expanded", "true");

                        if(e.type != "click") {
                            setTimeout(function() {
                                $("#gb-search-input").focus();
                            }, 500);
                        }
                    }
                }
            });

            // SEARCH SUBMIT
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "") {
                	window.location.href = "/site/Default.aspx?PageType=6&SiteID=8&SearchString=" + $("#gb-search-input").val();
                }
            });

            // SEARCH FOCUS OUT
            $("#gb-search").focusout(function() {
            	setTimeout(function () {
                	if(!$("#gb-search-toggle").is(":focus") && !$("#gb-search").find(":focus").length) {
                        $("#gb-search").removeClass("open").attr("aria-hidden", "true");
                        $("#gb-search-toggle").attr("aria-expanded", "false");
                    }
                }, 500);
            });
        },

        "ModEvents": function(){
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });
                eventsByDay(".upcomingevents .ui-articles");

            }
            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    }
                    if(!$(".sw-calendar-block-time", this).length) {
                		$(".ui-article-description", this).prepend('All Day');
                	}
                });

                $(".upcomingevents .ui-article").each(function(k, v){
                   	var dayOfWeek = $(".joel-day", v);
                	$(dayOfWeek).appendTo($('.upcoming-column.left', v));
                });

                $(".upcomingevents .ui-article").each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);	
                });
        	}
        },

        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": case "768": case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

