link.script:
function waitForElement() {
    if (typeof videojs !== "undefined") {
        videojs.options.flash.swf = "//vjs.zencdn.net/5.8.0/video-js.swf";
        videojs('video', {}, function () {
            videojs.ga();
        });
    }
    else {
        setTimeout(function () {
            waitForElement();
        }, 1);
    }
}
$(document).ready(function () {
    $(".embed_container h3").click(function () {
        var con = $(this).find(" ~ div")[0];
        if ($(con).css("height") == "0px") {
            $(con).css("height", "auto");
        }
        else {
            $(con).css("height", "");
        }
    });
});

