                if (getCookie('AngeloSiteAlertCookie-19b15c238b984675a1ac75c38daf6987-59ac04cbbcc94b809e68c8352f06685d-20200325-14491009') == "") {
                    $.dialog('/sys/public/sitealerts/display', { alertId: '59ac04cbbcc94b809e68c8352f06685d' })
                        .done(function () {
                    
                            setCookie('AngeloSiteAlertCookie-19b15c238b984675a1ac75c38daf6987-59ac04cbbcc94b809e68c8352f06685d-20200325-14491009', "sitealert", 365)
                        });
                }

            
    });


    function setCookie(cname, cvalue, exdays) {
        var d = new Date();
        d.setTime(d.getTime() + (exdays * 24 * 60 * 60 * 1000));
        var expires = "expires=" + d.toUTCString();
        document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
    }

    function getCookie(cname) {
        //debugger;
        var name = cname + "=";
        var decodedCookie = decodeURIComponent(document.cookie);
        var ca = decodedCookie.split(';');
        for (var i = 0; i < ca.length; i++) {
            var c = ca[i];
            while (c.charAt(0) == ' ') {
                c = c.substring(1);
            }
            if (c.indexOf(name) == 0) {
                return c.substring(name.length, c.length);
            }
        }
        return "";
    }
