                    dropdownHTML += "";
                dropdownHTML += "";


                //ADD A CLASS TO THE CHANNEL FOR NUMBER OF COLUMNS
                $(this).addClass("columns-"+numColumns);
                //INSERT NEW DROPDOWN CODE INTO THE PAGE
                $(this).find(".sw-channel-dropdown").html(dropdownHTML);
            })

            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
            	$(".sw-channel-dropdown").hide();
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");

                $(this).removeClass("hover");
            });

            $(".column-item a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case template.KeyCodes.left:
				case template.KeyCodes.up:
					e.preventDefault();
                    // IS FIRST ITEM
                    if($(this).parent().prev("li").hasClass("category-heading")){
                        if($(this).parent().prev("li").is(":first-child")){
                        	if($(this).parent().parent().parent().is(":first-child")){
                            	// FOCUS LAST SOCIAL ICON
                            	$("li.sw-channel-item.hover").find(".dd-social-icons li:last-child > a").focus();
                            } else {
                            	$(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                            }

                        } else {
                            $(this).parent().prev("li").prev("li").find("> a").focus();
                        }

                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case template.KeyCodes.right:
				case template.KeyCodes.down:
					e.preventDefault();
                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        //IS LAST ITEM IN LAST COLUMN
                        if($(this).parent().parent().parent().is(":last-child")){
                            // FOCUS FIRST SOCIAL ICON
                            $("li.sw-channel-item.hover").find(".dd-social-icons li:first-child > a").focus();
                        } else {
                        	$(this).parent().parent().parent().next().find(".column-item:nth-child(2) > a").focus();
                        }
                    } else {
                        // FOCUS NEXT ITEM
                        if($(this).parent().next("li").hasClass("category-heading")){
                        	$(this).parent().next("li").next("li").find("> a:first-child").focus();
                        } else {
                        	$(this).parent().next("li").find("> a:first-child").focus();
                        }

                    }


				break;

				// CONSUME TAB KEY
				case template.KeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
							// FOCUS DROPDOWN BUTTON
							$(".top-global-icons li:last-child > a").focus();
						} else {
							// FOCUS PREVIOUS ITEM
							$(".sw-channel-item.hover").prev().find("> a").focus();
						}
					} else {
						e.preventDefault();

                        $(".sw-channel-item.hover").next().find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case template.KeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM IN FIRST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
				break;

				// CONSUME END KEY
				case template.KeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM IN LAST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:last-child .column-item:last-child > a").focus();
				break;

				case template.KeyCodes.esc:
					$(".sw-channel-item.hover").find("> a").focus();
				break;
			}

          });

          $(".dd-social-icons a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case template.KeyCodes.left:
				case template.KeyCodes.up:
					e.preventDefault();
                    // IS FIRST ITEM
                    if($(this).parent().is(":first-child")){
                    	$("li.sw-channel-item.hover").find(".dropdown-column:last-child .column-item:last-child > a").focus();
                    } else {
                        // FOCUS PREVIOUS ITEM
                        $(this).parent().prev("li").find("> a").focus();
                    }
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case template.KeyCodes.right:
				case template.KeyCodes.down:
					e.preventDefault();
                    // IS LAST ITEM
                    if($(this).parent().is(":last-child")) {
                        $("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
                    } else {
                       $(this).parent().next("li").find("> a").focus();
                    }


				break;

				// CONSUME TAB KEY
				case template.KeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
							// FOCUS DROPDOWN BUTTON
							$(".top-global-icons li:last-child > a").focus();
						} else {
							// FOCUS PREVIOUS ITEM
							$(".sw-channel-item.hover").prev().find("> a").focus();
						}
					} else {
						e.preventDefault();

                        $(".sw-channel-item.hover").next().find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case template.KeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM IN FIRST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
				break;

				// CONSUME END KEY
				case template.KeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM IN LAST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:last-child .column-item:last-child > a").focus();
				break;

				case template.KeyCodes.esc:
					$(".sw-channel-item.hover").find("> a").focus();
				break;
			}

          });
        },

        "Body": function() {
            // FOR SCOPE
