                $(".ui-widget.app.pagenavigation .ui-widget-header").html("
");
            }   
        },

        "Homepage": function() {
            // FOR SCOPE
            var _this = this;

            //ARTICLE SLIDERS
            $("#sw-content-container1.ui-hp .ui-widget.app").csArticleSlider({
                "viewerNum": [4, 2, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            $("#sw-content-container5.ui-hp .ui-widget.app").csArticleSlider({
                "viewerNum": [3, 2, 1, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            //REGIONS B & F HEADLINES
           $(".hp-row.one .headlines li, .hp-row.three .headlines li").each(function(){
                //WRAP HEADLINE TEXT
                $(this).find(".ui-article-title, .ui-article-description, .ui-article-controls").wrapAll("

");
                $(this).find(".ui-article-description").wrap("

");
                $(this).find(".ui-article-description").attr("aria-hidden","true");
            })

            //CONTENT BACKGROUND IMAGE
            if(true){
            	var hpImageSrc = jQuery.trim("/cms/lib/TX50000022/Centricity/Template/GlobalAssets/images//logos/ISDSmall220.png");
                var hpImageSrcSplit = hpImageSrc.split("/");
                var hpImageSrcSplitLen = hpImageSrcSplit.length;
                if((hpImageSrc != "") && (hpImageSrcSplit[hpImageSrcSplitLen - 1] != "default-man.jpg")) {
                    $(".hp-row.two").css("background-image","url('/cms/lib/TX50000022/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg')");
                } else {
                    $(".hp-row.two").css("background-image","url('/cms/lib/TX50000022/Centricity/Template/18/default-images/default-hp-bg.jpg')");
                } 
            }

            //MORE LINKS IN ROW TWO
            $(".hp-row.two .app").each(function(){
                if($(this).find(".more-link").length){
                    $(this).find(".more-link").appendTo($(this).find(".ui-widget-header").addClass("more-link-active"));
                }
            })

            //HOVERS
            $(".hp-row.one .headlines li, .hp-row.three .headlines li").hover(function() {
                $(this).addClass("hover");
                $(this).find(".ui-article-description").attr("aria-hidden","false");
            }, function() {
                $(this).removeClass("hover");
                $(this).find(".ui-article-description").attr("aria-hidden","true");
            });

            //FOCUS
            $(".hp-row.one .headlines li .ui-article-title a, .hp-row.three .headlines li .ui-article-title a").focus(function() {
                $(this).closest("li").addClass("hover");
                $(this).closest("li").find(".ui-article-description").attr("aria-hidden","false");
            });

            $(".hp-row.one .headlines li .ui-article-title a, .hp-row.three .headlines li .ui-article-title a").focusout(function() {
                $(this).closest("li").removeClass("hover");
                $(this).closest("li").find(".ui-article-description").attr("aria-hidden","true");
            });

            //MAIN CONTENT HEADLINES
            $(".hp-row.two .headlines").each(function(){
                $(this).find(".ui-article").each(function(){
                    $(this).find(".ui-article-title, .ui-article-description, .ui-article-controls, .clear").wrapAll("
");
                })
            })
        },

        "moreCalendarLinkWidths": function(){

        	// FOR SCOPE
            var _this = this;

        	//MORE AND CALENDAR LINK WIDTHS ON ARTICLE SLIDERS
            $(".ui-hp .app").each(function(){
                if($(this).find(".cs-article-slider").length){
                    var buttonWidth = $(this).find(".cs-article-viewer").width() - 16;

                    switch(_this.GetBreakPoint()) {
                        case "desktop": 
                            $(this).find(".view-calendar-link").css("width",buttonWidth);
                            $(this).find(".more-link").css("width",buttonWidth-26);
                        break;
                        case "768":
                            $(this).find(".view-calendar-link").css("width",buttonWidth);
                            $(this).find(".more-link").css("width",buttonWidth+8);
                        break;
                    }

                }
            })
        },

