        "SocialTabs": function() {
            // FOR SCOPE
            var template = this;

            //REMOVE FEEDS / BUTTONS NOT IN USE
            $("#social-media-tabs [data-toggle='false']").remove();
            $("#social-media-feeds [data-toggle='false']").remove();

            //PLACE SOCIAL FEEDS IN THE CORRECT CONTAINERS
            var feedCount = 0;

            $("#social-feed-holding #sw-content-container3 > div").each(function(){
                $(this).find(".ui-article span").appendTo("#social-media-feeds > div:eq("+feedCount+")");
                feedCount ++;
            })

            //SET THE FIRST BUTTON AND FEED TO ACTIVE
            $("#social-media-tabs > div:first-child").addClass("active").attr("aria-selected","true");
            $("#social-media-feeds > div:first-child").addClass("active").attr("aria-hidden","false");

            //SOCIAL TAB NAV
            $(".social-tab").on('focus', function(){
                var tabNum = $(this).index();

                $(".social-tab").removeClass("active").attr("tabindex","-1");
                $(this).addClass("active").attr("tabindex","0");

                $(".social-media-feed-container").hide().removeClass("active").attr("aria-hidden","true");
                $(".social-media-feed-container").eq(tabNum).show().addClass("active").attr("aria-hidden","false");
            })

            $(".social-tab").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".social-tab:last-child").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).prev().focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".social-tab:first-child").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).next().focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".social-tab:first-child").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".social-tab:last-child").focus();
                    break;
                }
            });
        },

        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            // MOVE Bb FOOTER STUFF
            $(".gb-bb-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-bb-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-bb-footer.copyright").append($("#sw-footer-copyright").html());

            //ADD SITENAME TO FOOTER
            var siteNameOne = '';
            var siteNameTwo = '';

            if($.trim(siteNameOne) == "" || $.trim(siteNameTwo) == ""){
                $("#gb-contact-info").prepend("
Boerne Independent School District");
            } else {
                $("#gb-contact-info").prepend("
"+siteNameOne+"
 "+siteNameTwo+"");
            }

            //BACK TO TOP
            $("#to-top").click(function(e){
                e.preventDefault();
                $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                    $("li.sw-channel-item:first-child > a").focus();
                });
            });

            //REMOVE UNUSED SOCIAL ICONS
            var socialIconToggles = [true, true, true, true, false, false, false, false, false];

            for(i = 0; i < socialIconToggles.length; i++){
                if(socialIconToggles[i] == true){
                    $(".footer-row.social").show();
                }
            }

            $(".social-icon[data-toggle='false'], .footer-link[data-toggle='false']").remove();

            if($("#gb-footer-disclaimer span").text().trim() == ""){
                $("#gb-footer-disclaimer").hide();
            }

        },

