                "galleryOverlay" : false,
                "linkedElement" : [],  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade",
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {}, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {

                },
                "onTransitionEnd" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                    //WRAP CONTROLS
                    $(".mmg-control.back,.mmg-control.next").wrapAll("
");
                    $(".mmg-control.play-pause").wrap("
");

                    $(".mmg-control-wrapper.back-next").insertBefore(".mmg-control-wrapper.play-pause");

                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {

                } // props.element, props.mmgRecords
            });
        },

        "CheckSlideshow": function() {
            // FOR SCOPE
            var _this = this;

            if($(".hp").length && _this.GetBreakPoint() != "desktop") {
                if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(this.SlideshowDescFixed) {
                        $("#hp-slideshow").removeAttr("style");

                        this.SlideshowDescFixed = false;
                    }
                } else {
                    if(!this.SlideshowDescFixed) {
                        $("#hp-slideshow").css({
                            "height": $("#hp-slideshow").height(),
                            "overflow": "hidden"
                        });

                        this.SlideshowDescFixed = true;
                    }
                }
            }
        },

        "ModEvents": function() {
            // FOR SCOPE
            var template = this;

            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //add a zero to the date if it is a single digit
					if( newDateTime[dateIndex].length == 1 ) newDateTime[dateIndex] = "0" + newDateTime[dateIndex];

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).appendTo($(this));
                    });

                    //WRAP DATE AND MONTH IN A CONTAINER
                    $(".jeremy-date, .joel-month", this).wrapAll("");
                }) 
            }

            //ADD NO EVENTS TEXT
            $(".upcomingevents").each(function(){
                if(!$(this).find(".ui-article").length){
                    $("

There are no upcoming events to display.").appendTo($(this).find(".ui-articles"));
                }
            });

            $(".upcomingevents .ui-widget-detail").show();

        },

