    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            //HOMEPAGE HEADLINES SETUP
            hfSliderParams = {};
            hfSliderParams.slideWidth = 407;
            hfSliderParams.screenWidth = $(window).width();
            hfSliderParams.count = 0;
            hfSliderParams.slideContainer = $("#hp-content-top .headlines ul");
            hfSliderParams.numFullSlides = Math.floor(hfSliderParams.screenWidth / hfSliderParams.slideWidth);

            //HOMEPAGE EVENTS SETUP
            evSliderParams = {};

            evSliderParams.screenWidth = $(window).width();
            evSliderParams.count = 0;
            evSliderParams.slideContainer = $("#hp-content-top .upcomingevents ul");


            this.SetTemplateProps();
            this.modMyStart();
            this.modEvents();
            this.Header();
            this.Body();
            this.Homepage();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.JsMediaQueries();
            this.HomepageHeadlines();
            this.HomepageEvents();
            this.AppAccordion();
            this.Search();
            csGlobalJs.OpenInNewWindowWarning();


            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
        	// FOR SCOPE
            var _this = this;

            $("#hp-content-top .headlines ul.ui-articles li").each(function(){
            	if(!$(".ui-article-thumb", this).length){
                    var defaultImage = '/cms/lib/VA02201702/Centricity/Template/GlobalAssets/images///images/default-headlines.jpg';

                    $(".ui-article", this).prepend('');
                    $(this).addClass("default-image");
                }
            }); 

            this.ChannelBar();
            this.AccessibilityFeatures();
        },

        "WindowResize": function() {
 			evSliderParams.screenWidth = $(window).width();
            this.JsMediaQueries();
            hfSliderParams.screenWidth = $(window).width();
            hfSliderParams.numFullSlides = Math.floor(hfSliderParams.screenWidth / hfSliderParams.slideWidth);
            this.HomepageHeadlines();
            this.HomepageEvents();

        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                	evSliderParams.numEventsShown = 5;
                	if(evSliderParams.screenWidth >= 1280){
                		evSliderParams.slideWidth = 256;
                	} else {
                		evSliderParams.slideWidth = evSliderParams.screenWidth / evSliderParams.numEventsShown;
                	}

                	//SET DROPDOWN WIDTHSf
                	var ddWidth = $(".mystart-column.three").outerWidth();
                	$(".ac-dropdown-container .ac-dropdown").width(ddWidth);
                break;
                case "768":
					evSliderParams.numEventsShown = 3;
					evSliderParams.slideWidth = evSliderParams.screenWidth / evSliderParams.numEventsShown;
					//HOT TOPICS POSITION
					var mmrImageHeight = $("#hp-slideshow .rs-gallery-images-list li").height();
					var hotTopicsHeight = $("#hot-topics").height() + 17;
					$("#hot-topics").css("top",mmrImageHeight-hotTopicsHeight);

                    var logoWidth = $("#gb-logo").width();
            		$("#gb-sitename-one, #gb-sitename-two, #sp-content-top").css("padding-left",logoWidth);
                break;
                case "640":
					evSliderParams.numEventsShown = 3;
					evSliderParams.slideWidth = evSliderParams.screenWidth / evSliderParams.numEventsShown;
					//HOT TOPICS POSITION
					var mmrImageHeight = $("#hp-slideshow .rs-gallery-images-list li").height();
					var hotTopicsHeight = $("#hot-topics").height() + 13;
					$("#hot-topics").css("top",mmrImageHeight-hotTopicsHeight);

					var logoWidth = $("#gb-logo").width();
            		$("#gb-sitename-one, #gb-sitename-two, #sp-content-top").css("padding-left",logoWidth);
                break;
                case "480":
					evSliderParams.numEventsShown = 2;
					evSliderParams.slideWidth = evSliderParams.screenWidth / evSliderParams.numEventsShown;
					//HOT TOPICS POSITION
					var mmrImageHeight = $("#hp-slideshow .rs-gallery-images-list li").height();
					$("#hot-topics").css("top",mmrImageHeight);
                break;
                case "320":
					evSliderParams.numEventsShown = 2;
					evSliderParams.slideWidth = evSliderParams.screenWidth / evSliderParams.numEventsShown;
					//HOT TOPICS POSITION
					var mmrImageHeight = $("#hp-slideshow .rs-gallery-images-list li").height();
					$("#hot-topics").css("top",mmrImageHeight);
                break;
            }

            function mmrControlPosition(){
              if($("#gb-page.hp").length){
                  var topPosition = $("#mobile-icons").offset();
                  var iconHeight = $("#mobile-icons").height();
                  var mmrWrapperHeight = $("#mmr-control-wrapper").height();
                  topPosition = topPosition.top - (mmrWrapperHeight+iconHeight+21);
                  $("#mmr-control-wrapper").css("top",topPosition);
              }	
            }

            function mmrControlPositionNoIcons(){
            	if($("#gb-page.hp").length){
                  var topPosition = $("#foreground-content").offset();
                  var mmrWrapperHeight = $("#mmr-control-wrapper").height();
                  topPosition = topPosition.top - (mmrWrapperHeight+67);
                  $("#mmr-control-wrapper").css("top",topPosition);
                }
            }
        },

        "modMyStart": function() {

        	// ADD TRANSLATE
        	$("#ac-translate-list li").creativeTranslate({
                "type": 2,
                "brandedLayout": 2,
                "advancedOptions": {
                    "addMethod": "prepend",
                    "dropdownHandleText": "Translate",
                }

            });

            //BUILDING MY OWN SCHOOLLIST
	        var acSchoolList = '';

	        $(".sw-mystart-dropdown.schoollist li").each(function(){
	        	var schoolItem = $(this).html();
	            acSchoolList += "
"+schoolItem+"";
	        });  

	        //MOVING ANY MYSTART ELEMENTS TO MATCH DESIGN
	        $(".ac-dropdown-container.school-list .ac-dropdown-list").append(acSchoolList);
            $("#sticky-footer .gb-section").css("visibility","visible");

        },

        "Header": function() {
        	//HIDE AUDIENCE CHANNELS 
            var audienceArray = ['Parents','Students','Staff','Community']
            $(".sw-channel-item").each(function(){
            	var channelItem = $(this);
                var channelText = $(this).find("> a span").text();

                for(i = 0; i < audienceArray.length; i++){
                	if(audienceArray[i] == channelText){
                    	$(channelItem).addClass("audience-channel");
                    }
                }

            })

            $("#gb-channel-list").show();

            // ADD SITENAME
            if($("#gb-logo").is(":visible")){
            	var logoWidth = $("#gb-logo").width();
            } else {
            	var logoWidth = 0;
            }

            var siteNameOne = $.trim("Winchester");
            var siteNameTwo = $.trim("Public Schools");
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Winchester Public Schools";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = $.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = $.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = $.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename-one").text(siteNameOne).css("padding-left",logoWidth).show();
                $("#gb-sitename-two").text(siteNameTwo).css("padding-left",logoWidth).show();
            }
            	$("#sp-content-top").css("padding-left",logoWidth);

            $(document).scroll(function() {		

				var distanceFromTop = $(this).scrollTop();

				if(distanceFromTop >= 80){
					$("#gb-page").addClass("sticky-header");
				} else {
					$("#gb-page").removeClass("sticky-header");
				}

				var showToTop = $(document).height() / 2;

				if(distanceFromTop >= showToTop){
					$("#gb-page").addClass("half-way");
				} else {
					$("#gb-page").removeClass("half-way");
				}

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css("margin-top", $("header").outerHeight(true));
                    }
                }

                $("#sw-maincontent").css({
                    "display": "block",
                    "position": "relative",
                    "top": "-" + $("header").outerHeight(true) + "px"
                });

			});

        },

        "ChannelBar": function() {

            //BUILD AN ARRAY OF ELEMENTS
            var channelElements = [
            	[//HOME CHANNEL

                ],
                [//CHANNEL ONE
                    6,
                    6,
                    6
                ],
                [//CHANNEL TWO
                    6,
                    6,
                    7
                ],
                [//CHANNEL THREE
                    3,
                    2,
                    2
                ],
                [//CHANNEL FOUR
                    2,
                    2,
                    2
                ],
                [//CHANNEL FIVE
                    3,
                    3,
                    3
                ],
                [//CHANNEL SIX
                    3,
                    3,
                    3
                ],
                [//CHANNEL SEVEN
                    3,
                    3,
                    3
                ],
            ];

            //LOOP THROUGH CHANNELS THAT HAVE DROPDOWNS
            $(".sw-channel-item:not(.audience-channel) > a[aria-haspopup='true']").each(function(){
                var channelNumber = $(this).parent().index();

                //CHECK TO SEE HOW MANY COLUMNS THIS CHANNEL WILL HAVE IN THE DROPDOWN
                //AND HOW MANY SECTIONS WILL BE IN EACH COLUMN
                var numColumns = 0;

                //LOOP THROUGH THE COLUMN ELEMENTS IN THE CHANNEL ARRAY
                for (i = 0; i <= 2; i++) { 
                    var numSections = channelElements[channelNumber][i];

                    if(numSections == 0){
                        //DO NOTHING

                    } else {
                        //ADD ONE TO THE numColumns VARIABLE EACH TIME THERE IS TEXT FOUND IN THE COLUMN TITLE
                        numColumns++;
                    }
                }

                    //ADD CLASS TO THE DROPDOWN FOR HOW MANY COLUMNS
                    $(this).parent().find(".sw-channel-dropdown").addClass("column-"+numColumns);

                    //LOOP THROUGH EACH COLUMN AND BUILD THE STRUCTURE FOR IT
                    var dropDownHTML = '';
                    for(i = 0; i < numColumns; i++){
                        var numSections = channelElements[channelNumber][i];
                        var sectionCode = '';


                         $(this).parent().find(".sw-channel-dropdown li:not(.tagged)").each(function(){
                            if($(this).index() < numSections){	
                                sectionCode += "
"+$(this).html()+"";
                                $(this).addClass("tagged");
                            }
                        })

                        //REMOVE SECTIONS THAT WERE ADDED TO NEW STRUCTURE SO WE CAN
                        //BEGIN OUR INDEX AT 0 AGAIN THE NEXT TIME THROUGH THE LOOP
                        $(".sw-channel-dropdown li.tagged").remove();

                        dropDownHTML += "
"+
                                            ""+
                                               sectionCode+
                                            ""+
                                        "";
                    }


                    //ADD DROPDOWN CODE INTO PAGE
                     $(this).parent().find(".sw-channel-dropdown").html('
'+
                    	''+
                    		'
Learning for All, Whatever it Takes'+
                        	dropDownHTML+
                            '
'+
                            	''+
                                	'
I want to...'+
                                    '
Apply for a job'+
                            		'
Register my child'+
                                    '
View the Calendar'+
                                    '
Sign up for Parent Portal'+
                            		'
'+
                                    	''+
										''+
										''+
										''+
										''+
										''+
										''+
									''+                           
                            	''+
                            ''+
                       ''+
                   '');
            })

            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
            	$(".sw-channel-dropdown").hide();
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");

                $(this).removeClass("hover");
            });
        },

        "modEvents": function(){
        	if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "yes",
		            dayWeek     : "yes"
		        });
		       eventsByDay(".upcomingevents .ui-articles");
		    }

		    function eventsByDay(container) {
			    var countLi = 0;
			    $(".ui-article", container).each(function(){
			        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
			            var moveArticle = $(this).html();
			            $(this).parent().prev().children().children().next().append(moveArticle);
			            $(this).parent().remove();
			        };
			    }); 

			    $(".ui-article", container).each(function(){
					var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
					var newDate = '';
					var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

					//if we have the day of the week make sure to include it.
					if( dateIndex > 1 ){ 
						newDate += newDateTime[0] + "";
					}

					//add in the month
					newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

					//wrap the date in a new tag
					newDate += '' + newDateTime[dateIndex] + '';

					//append the date and month back into their columns
					$('.upcoming-column.left h1', this).html(newDate);	


					//add an ALL DAY label if no time was given
					$('.upcoming-column.right .ui-article-description', this).each(function(){
						if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
							$(this).prepend('ALL DAY');
						}
						$(".sw-calendar-block-time", this).appendTo($(this));
					});

					//ABBREVIATE DAY
					var origDay = $(this).find(".joel-day").text();
					var shortDay = origDay.substring(0, 3);

					$(this).find(".joel-day").text(shortDay);

				}); 



			}  
        },

