                $(this).closest("li").find(".ui-article-description").attr("aria-hidden","false");
            });

            $("#hp-content-top .headlines .ui-article-title a, #gb-page.view-all-headlines-page .headlines .ui-article-title a").on('focusout', function () {
                $(this).closest("li").removeClass("hover");
                $(this).closest("li").find(".ui-article-description").attr("aria-hidden","true");
            });

            $("#hp-content-top .headlines li, #gb-page.view-all-headlines-page .headlines li").hover(function(e) {
                $(this).addClass("hover");
                $(this).find(".ui-article-description").attr("aria-hidden","false");
