			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(2) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-links li:eq(1) a"));
			$(".gb-bb-footer.links").append($("#sw-footer-copyright"));		

			// FOOTER LINKS
			$(".to-top").click(function(e){
				e.preventDefault();
	            		$("html, body").animate({ scrollTop: 0 }, "slow", function() {
	            			$("li.sw-channel-item:first-child > a").focus();
	            		});
			});


            $(".ac-dropdown-container .selector").on('click keypress', function(event){
                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    controlDropdowns(event);
                }
            })

            //GLOBALLY CLOSE DROPDOWNS WHEN HOVERED AWAY FROM
            $(".ac-dropdown-container").hover(function(e) {
                e.preventDefault();
            }, function() {

                $(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
                $(this).removeClass("open"); 
                $(this).find(".selector").attr("aria-expanded","false");
                $(this).parent().removeClass("open");
            })

            //MAKE FOOTER ARROWS INTO CONTROLS FOR DROPDOWNS
            $(".mystart-arrow-ghost").click(function(){
            	if($(this).hasClass("fif")){
                	$(".ac-mystart-button.find-it-fast").click();
                } else if($(this).hasClass("signin")){
                	window.location.assign($(".ac-mystart-button.signin a").attr("href"));
                } else {
                	$(this).parent().find(".ac-dropdown-container .selector").click();
                }

            })

            function controlDropdowns(event){
                var dropdownToOpen = $(event.currentTarget).parent().find(".ac-dropdown-list").attr("id");
                if($("#"+dropdownToOpen).parent().parent().hasClass("open")){
                    $("#"+dropdownToOpen).parent().attr("aria-hidden", "true").slideUp(300, "swing"); 
                    $("#"+dropdownToOpen).parent().parent().removeClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","false");
                    $("#"+dropdownToOpen).closest(".mystart-column").removeClass("open");

                } else {
                    $("#"+dropdownToOpen).parent().attr("aria-hidden","false").slideDown(300, "swing", function(){
                        setTimeout(function(){
                            $("#"+dropdownToOpen).find("li:first a").focus();
                        }, 100);
                    });
                    $("#"+dropdownToOpen).parent().parent().addClass("open");
                    $("#"+dropdownToOpen).parent().prev().attr("aria-expanded","true");
                    $("#"+dropdownToOpen).closest(".mystart-column").addClass("open");
                }
            }

            //BACK TO TOP
            $("#to-top").click(function(e){
                e.preventDefault();
                        $("html, body").animate({ scrollTop: 0 }, "slow", function() {
                            $("li.sw-channel-item:first-child > a").focus();
                        });
            });

            //FIND IT FAST
            $(".ac-mystart-button.find-it-fast").on('click keypress', function(event){
	            if(template.AllyClick(event) === true){
	            	if($(this).parent().hasClass("open")){
	            		$("#find-it-fast-menu").slideUp(300, "swing");
	            		$(this).parent().removeClass("open");
	            		$(this).attr("aria-expanded","false");
                        $("#find-it-fast-menu").attr("aria-hidden","true");
	            	} else {
	            		$("#find-it-fast-menu").slideDown(300, "swing", function(){
	                        setTimeout(function(){
	                            $(".fif-tab.active").focus();
	                        }, 100);
	                    });
	                    $(this).attr("aria-expanded","true");
	                    $(this).parent().addClass("open");
                        $("#find-it-fast-menu").attr("aria-hidden","false");
	            	}
	            }
            })

            if(!$(".mobile").length){
            	$("#find-it-fast-menu").hover(function(e) {
                    e.preventDefault();
                }, function() {
                    closeFIF();
                })
            }


            function closeFIF(){
            	$("#find-it-fast-menu").slideUp(300, "swing");
	            $(".mystart-column.one").removeClass("open");
	            $(".ac-mystart-button.find-it-fast").attr("aria-expanded","false");
                $("#find-it-fast-menu").attr("aria-hidden","true");
            }

            //TAB NAVIGATION
            $(".fif-tab").on('focus', function(){
	        	var tabNum = $(this).index();

	        	$(".fif-tab").removeClass("active").attr({
	        		"tabindex":"-1",
	        		"aria-selected":"false"
	        	});
	        	$(this).addClass("active").attr({
	        		"tabindex":"0",
	        		"aria-selected":"true"
	        	});

	        	$(".fif-icons").hide().removeClass("active").attr("aria-hidden","true");
	        	$(".fif-icons").eq(tabNum).show().addClass("active").attr("aria-hidden","false");
	        })

