");
                    	$(".rs-gallery-desc, #mmr-control-bullets-wrapper").wrapAll("
");

                        $(".rs-gallery-desc").append(""+
                            ""+
                                ""+
                            ""+
                        "");

                    	$(".rs-gallery-desc-more-link a").on('focus', function () {
			                $("#mmr-items-container").addClass("hover");
			            });

			            $(".rs-gallery-desc-more-link a").on('focusout', function () {
			                $("#mmr-items-container").removeClass("hover");
			            });

                        checkForDesc();
                    }
                });
            }

            function checkForDesc(){
            	if($(".rs-gallery-desc").hasClass("hidden")){
                	$("#gb-page").addClass("no-mmr-desc");
                } else {
                	$("#gb-page").removeClass("no-mmr-desc");
                }
            }
