        "Search": function() {
        	// FOR SCOPE
            var template = this;

            $("#gb-search-input").focus(function() {
                if($(this).val() == "SEARCH") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("SEARCH");
                }
            });

            // SUBMIT SEARCH
	        $("#gb-search-form").submit(function(e){
	            e.preventDefault();

	            window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input", this).val();
	        });
        },

        "AllyClick": function(event) {
            if(event.type === 'click'){
                return true;
            } else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if((code === 32)|| (code === 13)){
                    return true;
                }
            } else{
                return false;
            }
        },

        "AllyClickForButtons": function(event) {
            if(event.type === 'click'){
	            return true;
	        }
	        else if(event.type === 'keypress'){
	            var code = event.charCode || event.keyCode;
	            if(code === 13){
	                return true;
	            }
	        }
	        else{
	            return false;
	        }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "AccessibilityFeatures": function(){
        	$("#sw-mystart-mypasskey").attr({
                "aria-label":	"Open My Passkey",
                "role":		"button"
            });

            //REMOVE ALL HIDDEN LINKS AND HEADINGS
        	$("a[data-toggle='false'], a[data-toggle='#']").remove();
            $('.disclaimer-link').css("display","block");

            if($("#footer-tagline h2").text() == ""){
            	$("#footer-tagline h2").remove();
            }

            if($("#footer-tagline h3").text() == ""){
            	$("#footer-tagline h3").remove();
            }


            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

            // KEY MAPPING
            var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            //GLOBAL KEYBOARD NAV FOR DD SELECTORS
            $(".ac-dropdown-container .selector").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                        } else {
                            e.preventDefault();

                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");


                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true;
                            }

                            switch(parentContainer){
                                case "user-options":
                                    $("#gb-search-input").focus();
                                break;
                                case "translate":
                                    if(loggedIn == true){
                                    	$(".ac-dropdown-container.user-options .selector").focus();
                                    } else {
                                    	$(".ac-mystart-button.signin a").focus();
                                    }
                                break;
                                case "school-list":
                                	$(".ac-dropdown-container.translate .selector").focus();

                                break;
                            }

                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.down:
                    case csKeyCodes.right:
                        e.preventDefault();

                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR DD LINKS
            $(".ac-dropdown-container li a").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();

                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true;
                            }

                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                	$(".ac-dropdown-container.school-list .selector").focus();
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                      $(".ac-dropdown-container.translate .selector").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                      $(".ac-mystart-button.home a").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;
                            }
                        } else {
                            e.preventDefault();
                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true;
                            }
                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                	if(loggedIn){
                                      	$(".ac-dropdown-container.user-options .selector").focus();
                                      	$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      	$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                        $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      	$(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                    } else {
                                    	$(".ac-mystart-button.signin a").focus();
                                    	$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      	$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                        $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      	$(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                    }

                                break;

                                case "user-options":
                                      $("#gb-search-input").focus();
                                      $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                    	$(".ac-dropdown-container.translate .selector").focus();
                                      	$(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                      	$(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                        $(".ac-dropdown-container."+parentContainer+"").parent().removeClass("open");
                                      	$(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;
                            }


                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                    break;
                }
            });


            //GLOBAL ICONS
           $(".cs-global-icon, #find-it-fast-menu .social-icon").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case csKeyCodes.esc:
                        $("#find-it-fast-menu").slideUp(300, "swing");
                        $(".mystart-column.one").removeClass("open");
                        $(".ac-mystart-button.find-it-fast").attr("aria-expanded","false");
                        $("#find-it-fast-menu").attr("aria-hidden","true");
                        $(".ac-mystart-button.find-it-fast").focus();
