			//FIF TABS
		$(".fif-tab").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".fif-tab:last-child").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).prev().focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".fif-tab:first-child").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).next().focus();
					}
				break;

                case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						$("#find-it-fast-menu").slideUp(300, "swing");
                        $(".mystart-column.one").removeClass("open");
                        $(".ac-mystart-button.find-it-fast").attr("aria-expanded","false");
                        $("#find-it-fast-menu").attr("aria-hidden","true");
                        $(".ac-mystart-button.find-it-fast").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".fif-tab:first-child").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".fif-tab:last-child").focus();
				break;

				case csKeyCodes.esc:
					$("#find-it-fast-menu").slideUp(300, "swing");
	            	$(".mystart-column.one").removeClass("open");
	            	$(".ac-mystart-button.find-it-fast").attr("aria-expanded","false");
                    $("#find-it-fast-menu").attr("aria-hidden","true");
	            	$(".ac-mystart-button.find-it-fast").focus();
				break;
			}
		});

        //CHANNEL BAR DROPDOWNS
        $(".column-item a").keydown(function(e) {
            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();
                    if($(this).hasClass("social-icon")){
                    	//IS SOCIAL ICON
                        if($(this).is(":first-child")){
                        	// FOCUS LAST NON SOCIAL ITEM IN LAST COLUMN
                            $("li.sw-channel-item.hover").find(".dropdown-column:last-child .column-item:nth-last-child(2) > a").focus();
                        } else {
                        	$(this).prev().focus();
                        }

                    } else {
                    	// IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // IS FIRST ITEM IN FIRST COLUMN
                            if($(this).parent().parent().parent().is(":nth-child(2)")){
                                // FOCUS LAST ITEM IN LAST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:last-child .column-item:last-child > a:last-child").focus();
                            } else {
                                // FOCUS LAST ITEM IN PREVIOUS COLUMN
                                $(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                            }

                        } else {
                            if($(this).parent().is(":nth-child(2)") && $(this).parent().hasClass("want-to-link")){
                                // FOCUS LAST ITEM IN PREVIOUS COLUMN
                                $(this).parent().parent().parent().prev().find(".column-item:last-child > a").focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).parent().prev("li").find("> a").focus();
                            }

                        }
                    }


				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();
                    if($(this).hasClass("social-icon")){
                    	//IS SOCIAL ICON
                    	if($(this).is(":last-child")){
                        	// FOCUS FIRST ITEM IN FIRST COLUMN
                            $("li.sw-channel-item.hover").find(".dropdown-column:nth-child(2) .column-item:first-child > a").focus();
                        } else {
                        	$(this).next().focus();
                        }
                    } else {
                    	// IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            //IS LAST ITEM IN LAST COLUMN
                            if($(this).parent().parent().parent().is(":last-child")){
                                // FOCUS FIRST ITEM IN FIRST COLUMN
                                $("li.sw-channel-item.hover").find(".dropdown-column:nth-child(2) .column-item:first-child > a").focus();
                            } else {
                                if($(this).parent().parent().parent().is(":nth-last-child(2)")){
                                    // FOCUS FIRST ITEM IN NEXT COLUMN
                                    $(this).parent().parent().parent().next().find(".column-item:nth-child(2) > a").focus();
                                } else {
                                    // FOCUS FIRST ITEM IN NEXT COLUMN
                                    $(this).parent().parent().parent().next().find(".column-item:first-child > a").focus();
                                }

                            }
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a:first-child").focus();
                        }
                    }

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						e.preventDefault();
						// IS FIRST ITEM
						if($(this).parent().parent().parent().parent().parent().is(":first-child")) {
							// FOCUS DROPDOWN BUTTON
							$(".global-icon:last-child").focus();
						} else {
							// FOCUS PREVIOUS ITEM
							$(".sw-channel-item.hover").prev().find("> a").focus();
						}
					} else {
						e.preventDefault();

                        $(".sw-channel-item.hover").next().find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM IN FIRST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:first-child .column-item:nth-child(2) > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM IN LAST COLUMN
					$("li.sw-channel-item.hover").find(".dropdown-column:nth-last-child(2) .column-item:last-child > a").focus();
				break;

				case csKeyCodes.esc:
					$(".sw-channel-item.hover").find("> a").focus();
				break;
			}

          });







        }
