link.script:
$(document).ready(function() {
	$('.main-slider').slick({
		dots: false,
		fade: true,
		infinite: true,
		adaptiveHeight: true, 
		autoplay: true,
		autoplaySpeed: '4000',
		arrows: true,
		appendArrows:'.sliderNav',
		prevArrow:'Previous Slide',
		nextArrow:'Next Story',
	});
	if ($('.white-popup').length) {
		$('.slide-pop').magnificPopup({
		  disableOn: 700,
		  type: 'inline',
		  midClick: true,
		  mainClass: 'mfp-fade',
		  removalDelay: 160,
		  preloader: false,
		  fixedContentPos: false
		});
	}
});
