            $(".sw-mystart-dropdown").off();
            $(".sw-mystart-dropdown").click(function(){
                $(this).toggleClass("open");
                $(".sw-dropdown-arrow",this).toggleClass("open");
                $(".sw-dropdown", this).slideToggle("slow", "swing");
            });

            //DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
            $("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown").attr("aria-hidden","true");
            $("#sw-mystart-account").off();
            $("#sw-mystart-account").click(function(){
                $(".icon-font-arrow",this).toggleClass("open");
                $("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
                $("#sw-myaccount", this).attr("aria-expanded", ($("#sw-myaccount", this).attr("aria-expanded") == "true") ? "false" : "true");
            });

            $("#cs-sw-myaccount-list li").each(function(){
                $("> a", this).attr("tabindex", "0"); 
            }); 

            $("#sw-myaccount").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();


                        $(this).next("#cs-sw-myaccount-list").slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                        if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                            $(this).addClass("open").attr("aria-expanded","true");
                            $(this).next("#cs-sw-myaccount-list").find("li:first-child a").focus();
                        } else {
                            $(this).removeClass("open").attr("aria-expanded","false");
                        }
                        // FOCUS FIRST ITEM

                    break;
                }
            });    

            $("#cs-sw-myaccount-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    break;
                }
            });

            $('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

            this.Translator();

            $(".sw-mystart-dropdown.translate").each(function(i) {
                // SET ATTRIBUTES
                $(".sw-dropdown-list", this).attr("role", "menu");
                $(".sw-dropdown-list li", this).attr("role", "menuitem");
            });

            $(".sw-mystart-dropdown.translate .sw-dropdown-list li a").keydown(function(e) {

                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                        if(e.shiftKey && e.keyCode == csKeyCodes.tab) {
                        	if($(this).parent().is(":first-child")){
                                e.preventDefault();

                                // FOCUS THE SCHOOLS BUTTON
                                $("#gb-mystart button#gb-translate-trg").focus().attr({
                                    "aria-expanded": "false"
                                }).removeClass("active");
                                $("#gb-translate-outer").attr("aria-hidden","true").slideUp( "slow", function() {});
                                $("#gb-translate-outer .sw-mystart-dropdown .sw-dropdown").attr("aria-hidden","true");
                            }
                        }
            });            

            $(".sw-mystart-dropdown.translate .sw-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        // FOCUS THE SCHOOLS BUTTON
                        $("#gb-mystart button#gb-translate-trg").focus().attr({
                            "aria-expanded": "false"
                        }).removeClass("active");
                        $("#gb-translate-outer").attr("aria-hidden","true").slideUp( "slow", function() {});
                        $("#gb-translate-outer .sw-mystart-dropdown .sw-dropdown").attr("aria-hidden","true");
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;
                }
            });

            $("#sw-myaccount").attr({"role": "button", "aria-expanded": "false", "aria-haspopup": "true"});

