        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/CO01001723/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                    $("#gb-footer-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                    $("#gb-footer-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('Greeley-Evans');
            var siteNameTwo = jQuery.trim('Weld County School District 6');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "Greeley-Evans School District 6";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
                $("#gb-footer-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
                $("#gb-footer-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
                $("#gb-footer-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
                $("#gb-footer-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }

            var tagline = jQuery.trim('');
                    var channelHeight = $("#gb-channel-list").height();
                    var taglineTop = channelHeight + 39;
                    if(tagline != "" && !$("#gb-tagline p").length){
                        $("#hp-slideshow .rs-gallery-container .rs-gallery-images").append('

' + tagline + '');
                    }

        },
