         "StickyChannelBar": function() {
            var channelHeight = $("#sw-channel-list-container").height();

            if ($(window).scrollTop() <= channelHeight) {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    $(".wcm-controls").css("margin-top", "0px");
                }
            } else {

                if($(".sp").length && $(".ui-widget.app.calendar").length) {
                    if($(".wcm-controls").hasClass("wcm-stuck")) {
                        $(".wcm-controls").css({
                        		"margin-top": channelHeight + "px",
                                "z-index": "103"
                                });
                    }
                }
            }
        },
