                    e.preventDefault();
                    if($(this).hasClass("active")) {
                        $(this).removeClass("active")
                        $("#slideshow-links-dropdown").slideUp( "slow", function() {

                        }).attr("aria-hidden","true");
                        $(this).focus().attr({
                        	"aria-expanded": "false"    
                        });
                    } else {
                        $(this).addClass("active").attr({
                        	"aria-expanded": "true"     
                        });
                        $("#slideshow-links-dropdown").slideDown( "slow", function() {

                        }).attr("aria-hidden","false");
                        $("#slideshow-links-dropdown li:first-child a").focus();
                    }
                }
            });



            //Customize Apps
            this.UpcomingEvents();
        },
