        "CheckSlideshow": function() {
        	if($(".hp").length && this.GetBreakPoint() != "desktop") {
                if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(this.SlideshowDescriptionIsFixed) {
                        $("#hp-slideshow").removeAttr("style");

                        this.SlideshowDescriptionIsFixed = false;
                    }
                } else {
                    if(!this.SlideshowDescriptionIsFixed) {
                        $("#hp-slideshow").css({
                            "height": $("#hp-slideshow .rs-gallery-container").outerHeight(true),
                            "overflow": "hidden"
                        });

                        this.SlideshowDescriptionIsFixed = true;
                    }
                }
            }
        },
        "SLM":  function() {
            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 30447,
                'cache': false,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */



                    var elemSchools = '', middleSchools = '', highSchools = '', otherSchools = '', testSchools = '', schools = '', miscSchools = '';

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                        	if(schoolList[i].schoolType.toLowerCase() == 'elementary') {
                            	elemSchools += '
' + schoolList[i].schoolName + '';
                            } 
                            else if(schoolList[i].schoolType.toLowerCase() == 'middle') {
                            	middleSchools += '
' + schoolList[i].schoolName + '';
                            }
                            else if(schoolList[i].schoolType.toLowerCase() == 'high') {
                            	highSchools += '
' + schoolList[i].schoolName + '';
                            }
                            else if(schoolList[i].schoolType.toLowerCase() == 'alternative') {
                            	otherSchools += '
' + schoolList[i].schoolName + '';
                            }
                            else if(schoolList[i].schoolType.toLowerCase() == 'other') {
                            	miscSchools += '
' + schoolList[i].schoolName + '';
                            }
                        }
                    }

                    $('.gb-school-list-column.elem ul').append(elemSchools);
                    $('.gb-school-list-column.middle ul').append(middleSchools);
                    $('.gb-school-list-column.high ul').append(highSchools);
                    $('.gb-school-list-column.other ul').append(otherSchools);
                    $('.gb-school-list-column.misc ul').append(miscSchools);

                    // TAB HEADER CLICK
                    $("#gb-school-list .gb-school-list-header").click(function(e){
                        e.preventDefault();
                        // IF THE SELECTED TAB ISNT ALREADY ACTIVE
                        if(!$(this).hasClass("active")){
                            // GET CLASS OF SELECTED TAB
                            var selectedList = $(this).attr("class");
                            selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

                            //REMOVE CURRENT ACTIVE CLASSES
                            $("#gb-school-list .gb-school-list-section.active").attr("aria-hidden","true");
                            $("#gb-school-list .gb-school-list-header.active, #gb-school-list .gb-school-list-section.active").removeClass("active");


                            // SET NEW ACTIVE CLASSES
                            $(this).addClass("active");
                            $("#gb-school-list .gb-school-list-section." + selectedList).addClass("active").attr("aria-hidden","false");
                        }


                    });



                    // SCHOOL TAB KEYDOWN
                    $(".gb-school-list-header").keydown(function(e) {

                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                        var lastIndex = $(".gb-school-list-header").length - 1;

                        if(e.shiftKey && e.keyCode == csKeyCodes.tab) {
              e.preventDefault();

              // FOCUS THE SCHOOLS BUTTON
              $("#gb-mystart button#gb-schools-trg").focus().attr({
                            	"aria-expanded": "false"
                            }).removeClass("active");
                            $("#gb-school-list-outer").slideUp( "slow", function() {});
                        }

                        switch(event.keyCode){
                            // CONSUME LEFT AND UP ARROWS
                            case csKeyCodes.left:
                            case csKeyCodes.up:
                                event.preventDefault();

                                // FOCUS THE PREVIOUS TAB
                                if ($(this).index() == 0) { 
                                    $(".gb-school-list-header:last-child").focus();
                                } else { 
                                    $(".gb-school-list-header").eq($(this).index() - 1).focus();
                                } 
                            break;

                            // CONSUME RIGHT AND DOWN ARROWS
                            case csKeyCodes.right:
                            case csKeyCodes.down:
                                event.preventDefault();

                                // FOCUS THE NEXT TAB
                                if ($(this).index() == lastIndex) { 
                                    $(".gb-school-list-header:first-child").focus();
                                } else { 
                                    $(".gb-school-list-header").eq($(this).index() + 1).focus();
                                }
                            break;

                            // CONSUME HOME KEY
                            case csKeyCodes.home:
                                event.preventDefault();

                                // FOCUS THE FIRST TAB
                                $(".gb-school-list-header:first-child").focus();
                            break;

                            // CONSUME END KEY
                            case csKeyCodes.end:
                                event.preventDefault();

                                // FOCUS THE LAST TAB
                                $(".gb-school-list-header:last-child").focus();
                            break;
                        }

                    });

                    // SCHOOL TAB FOCUS
                    $(".gb-school-list-header").focus(function() {
                        // ADJUST TAB ATTRIBUTES
                        $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                        $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

                        // ADJUST PANEL ATTRIBUTES
                        $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
                        $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).removeClass("active");
                        $("#" + $(this).attr("aria-controls") + " li a" ).attr({"tabindex": "0"});
                    });

                    // SCHOOL TAB KEYDOWN
                    $(".gb-school-list-section li a").keydown(function(e) {

                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                        switch(event.keyCode){
                            // CONSUME LEFT AND UP ARROWS
                            case csKeyCodes.left:
                            case csKeyCodes.up:
                                event.preventDefault();

                                // IS FIRST ITEM
                                if($(this).parent().is(":first-child")) {
                                    // FOCUS LAST ITEM
                                    //$(this).closest("ul").find("li:last-child > a").focus();
                                    $("#gb-school-list-headers .gb-school-list-header.active").focus();
                                } else {
                                    // FOCUS PREVIOUS ITEM
                                    $(this).parent().prev("li").find("> a").focus();
                                }

                            break;

                            // CONSUME RIGHT AND DOWN ARROWS
                            case csKeyCodes.right:
                            case csKeyCodes.down:
                                event.preventDefault();

								// IS LAST ITEM
                                if($(this).parent().is(":last-child")) {
                                    // FOCUS FIRST ITEM
                                    //$(this).closest("ul").find("li:first-child > a").focus();
                                    $("#gb-school-list-headers .gb-school-list-header.active").focus();
                                } else {
                                    // FOCUS NEXT ITEM
                                    $(this).parent().next("li").find("> a").focus();
                                }  

                            break;

                             // CONSUME HOME KEY
                            case csKeyCodes.tab:
                                event.preventDefault();

                                /*$("#gb-mystart button#gb-schools-trg").focus().attr({
                            	"aria-expanded": "false"
                                }).removeClass("active");
                                $("#gb-school-list-outer").slideUp( "slow", function() {});*/
                            break;

                            // CONSUME HOME KEY
                            case csKeyCodes.home:
                                event.preventDefault();

                                // FOCUS FIRST ITEM
                        		$(this).closest("ul").find("li:first-child > a").focus();
                            break;

                            // CONSUME END KEY
                            case csKeyCodes.end:
                                event.preventDefault();

                                // FOCUS LAST ITEM
                        		$(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                            break;
                        }

                    });

                    window.csTemplate.RsMenu(schoolList);
                }
            });
        },
        "SlideshowLinks": function() {

        	var template = this
            var anyLinks = false;
        	var slideshowLinksList = '';
        	var slideshowLinks = [
            	["Volunteer in a school","https://www.greeleyschools.org/domain/3363","_self"],
                ["Enroll my child","https://www.greeleyschools.org/Domain/8915","_self"],
                ["Find info on blended learning","https://www.greeleyschools.org/Page/13456","_self"],
                ["Know more about District 6","https://www.greeleyschools.org/domain/6","_self"],
                ["Apply for free & reduced lunch","https://d6applyformeals.greeleyschools.org/","_self"],
                ["Find a video performance","https://www.greeleyschools.org/Page/14896","_self"],
                ["Look into our school diversity","https://www.greeleyschools.org/domain/4069","_self"],
                ["Get info on the Family Center","https://www.greeleyschools.org/Domain/6133","_self"],
                ["Contact Us","https://www.greeleyschools.org/domain/4266","_self"],
            ];

            slideshowLinksList += '

I Would Like To...'
            slideshowLinksList += '';

            for(var l = 0; l < 9; l++) {
            	if(slideshowLinks[l][0] != ""){
                	slideshowLinksList += '
' + slideshowLinks[l][0] + '';
            		anyLinks = true;
                }
            }

            slideshowLinksList += '';

            if(anyLinks != false){ // Adam - 11.27.17 - #02986086
            	$("#gb-extra-links-outer").prepend(slideshowLinksList);    	
            }



            $("#slideshow-links-dropdown li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("#slideshow-links-dropdown").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("#slideshow-links-dropdown").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        $("#slideshow-links-dropdown").slideUp( "slow", function() {

                        }).attr("aria-hidden","true");
                        $("#slideshow-links-trg").focus().attr({
                        	"aria-expanded": "false"    
                        });
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#slideshow-links-dropdown").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#slideshow-links-dropdown").find("li:last-child > a").focus();
                    break;
                }
            });

        },
