        "ChannelBar": function() {
            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList
                    	.attr('aria-hidden', 'false')
                    	.slideDown(300, "swing");
                    $('a', subList).attr('tabindex', 0);
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown")
                	.attr('aria-hidden', 'false')
                    .slideUp(300, "swing");
                $('.sw-channel-dropdown a').attr('tabindex', -1);
                $(this).removeClass("hover");
            });

            //DOC check if channel dropdown goes off screen
            $(".sw-channel-item").on('mouseenter mouseleave', function (e) { /* changed alot below - Jeremy K - Case #03123234 4.12.18 */
                if ($('ul.sw-channel-dropdown', this).length) {
                    $("ul.sw-channel-dropdown", this)
                    	.css('left', '')
                    	.show();

                    var elm = $('.sw-channel-dropdown', this);
                    var off = elm.offset();
                    var l = off.left;
                    var w = elm.width();
                    var docH = $("#sw-channel-list-container").height();
                    var docW = $("#sw-channel-list-container").width();
                    var winW = $(window).width() - 35;
                    var overflow = -((l + w) - winW + 80);

                    var isEntirelyVisibleR = (l + w <= winW);
                    var isNotEntirelyVisibleL = (-(overflow) + w >= winW);

                    if (!isEntirelyVisibleR || isNotEntirelyVisibleL) {
                        $(this).addClass('edge');

                        if( isNotEntirelyVisibleL ){
                        	overflow = -($(this).offset().left);
                        }

                        $(".mega-channel.sw-channel-item.edge ul.sw-channel-dropdown").css("left", overflow);
                    } else {
                        $(this).removeClass('edge');
                    }
                }
            });


            var specialChannelFound = 0;

            $('#channel-navigation > .sw-channel-item').each(function(channelItemIndex, channelItem){
            	if( $('> a > span', channelItem).text().toLowerCase() == "TSD LEARNS!".toLowerCase() ){

                    $('#gb-custom-channel .mystart-label').html('' + $('> a > span', channelItem).text() + ' ');

                    $('#gb-custom-channel #custom-section-list').html( $('.sw-channel-dropdown', channelItem).html() );

                    $('#gb-custom-channel #custom-section-list a').attr('tabindex', -1);

                    $(channelItem).remove();

                    specialChannelFound = 1;

                    return true
                }
            });

            if( !specialChannelFound ) $('#gb-custom-channel').remove();

            this.MegaMenu();

        },
