                if($(".mega-channel-cat", this).length <= 4) {
                    $(".sw-channel-dropdown", this).css("width","933");
                     $(this).addClass("col-count-4");
                }
                if($(".mega-channel-cat", this).length == 5) {
                    $(".sw-channel-dropdown", this).css("width","990");
                     $(this).addClass("col-count-5");
