                    CreativeTemplate.AppScroller();
                });

                $('#gb-global-icons-outer a').attr('tabindex', -1);
                $('#global-icon-trigger').click(function(e){
                	if( $(this).hasClass('active') ){
                    	$(this)
                        	.removeClass('active')
                            .attr('aria-expanded', 'false');
                        $('#gb-global-icons-outer')
                        	.slideUp()
                            .attr('aria-hidden', 'true');
                        $('#gb-global-icons-outer a').attr('tabindex', -1);
                    }else{
                    	$(this)
                        	.addClass('active')
                            .attr('aria-expanded', 'true');
                        $('#gb-global-icons-outer')
                        	.slideDown()
                            .attr('aria-hidden', 'false');
                        $('#gb-global-icons-outer a').attr('tabindex', 0);
                        $('#gb-global-icons-outer ul > li:first a').focus();
                    }
                });
            }else{
                CreativeTemplate.SameHeights();
            }

            //Customize Apps
            this.UpcomingEvents();
        },
