                	$('#hp-app-scroller .headlines-scroll-ctrl.next').click();
                }, 5000);


                $('#hp-app-scroller .ui-widget-detail').hover(function(e){
                	clearInterval(headerUeInterval);
                }, function(e){
                	if( $('#hp-app-scroller .headlines-play-pause-ctrl').hasClass('playing') ){
                        headerUeInterval = setInterval(function(e){
                            $('#hp-app-scroller .headlines-scroll-ctrl.next').click();
                        }, 5000);
                	}
                });

                $('#hp-app-scroller .headlines-play-pause-ctrl').click(function(e){
					if( $(this).hasClass('playing') ){
                    	clearInterval(CreativeTemplate.HeaderUeInterval);

                        $(this).removeClass('playing').addClass('paused');
                        $('span', this).html('Play Announcements');

                    }else{
                    	CreativeTemplate.HeaderUeInterval = setInterval(function(e){
                            $('#hp-app-scroller .headlines-scroll-ctrl.next').click();
                        }, 5000);

                        $(this).removeClass('paused').addClass('playing');
                        $('span', this).html('Pause Announcements');
                    }
                });
            }
        },


// APP METHODS
        "CalendarListView": function() {
            if($(".ui-widget.app.calendar").length) {
                // AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
                if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
                    $("a.ui-btn-toggle.list").click();
                    $("a.ui-btn-toggle.month").click();
                    $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
                }

                switch(this.GetBreakPoint()) {
                    case "desktop": 
                    case "768": 
                    case "640":
                        $(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
                    break;
                    case "480": 
                    case "320":
                        $(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
                        $(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
                        $(".ui-btn-toggle.month").removeClass("active");
                        $(".ui-btn-toggle.list").addClass("active");
                    break;
                }
            }
        },
        "UpcomingEvents": function(){
        	if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "yes"
                });

                $(".upcomingevents .ui-articles .ui-article").each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                });

                $(".upcoming-column-container").each(function(){
                    $(".upcoming-column.left", this).append('
');
                    $(".joel-day", this).appendTo($("h2", this));
                    $(".ui-article-description", this).each(function(){
                        $(".sw-calendar-block-title", this).prependTo($(this));
                        var time = $(".sw-calendar-block-time", this).text();
                        if(time != "") {
                            time = time.split(" ");
                            if(time.length == 2) {
                                $(".sw-calendar-block-time", this).text(time[0]+time[1]);
                            } else if (time.length == 5) {
                                $(".sw-calendar-block-time", this).text(time[0]+time[1]+" - "+time[3]+time[4])
                            }
                        }
                    });
                });
            }

            //ADD THE MORE-LESS BUTTON IF THERE ARE MORE THAN 4 EVENTS ON A DAY
            $("#hp-upcoming-events .ui-widget.app.upcomingevents .upcoming-column.right").each(function() {
                if($(".ui-article-description", this).length > 4) {
                    $(this).append('View More ');
                }
            });

            //CLICK FUNCTION FOR THE MORE-LESS BUTTON
            $(document).on("click keydown", ".hp-events-btn", function(e) {
                if(CreativeTemplate.A11yClick(e)) {
                    e.preventDefault();
                    if($(this).hasClass("active")) {
                        $(this)
                        	.attr('aria-expanded', 'false')
                        	.removeClass("active")
                            .siblings(".ui-article-description:nth-of-type(n+5)")
                            .slideUp(500, function(){
                            	CreativeTemplate.UpcomingSameHeights();
                            });
                        $("span", this).eq(0).text("View More");
                    } else {
                        $(this)
                        	.attr('aria-expanded', 'true')
                        	.addClass("active")
                            .siblings(".ui-article-description:nth-of-type(n+5)")
                            .slideDown(500);
                        $("span", this).eq(0).text("View Less");
                    }
                }
            });
        }
	};

