    var CreativeTemplate = {
        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "RsMenuSchools": { "Elementary Schools": [], "Middle Schools": [], "High Schools": [], "Educational Alternative Services": [], "Exceptional Centers": [], "Career Technical & Adult Education": [] },

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            csGlobalJs.OpenInNewWindowWarning();
            this.SetTemplateProps();
            this.Slideshow();
            this.mmgTitles();
            this.MyStart();
            this.JsMediaQueries();
            this.SchoolList();
            this.Translate();
            this.Header();
            this.Search();
            this.RsMenu();
            this.ContentRotators();
            this.Body();
            this.Homepage();
            this.ModEvents();
            this.GlobalIcons();
            this.SocialIcons();
            this.AppAccordion();
            this.Footer();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            $(window).bind('resizeEnd', function() {

            	var _this = this;
               if($(".mmg-control-wrapper.carousel").hasClass("paused")){
               		CreativeTemplate.mmgTitles();
                  $(".cs-article-slider-control-custom.play").click();
              }
            });
        },

        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

