        "MyStart": function() {
            // FOR SCOPE
            var _this = this;

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



            // SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
            	userOptionsItems += "
CHANGE / RESET PASSWORD";
                userOptionsItems += "
SCHOOLWIRES";
            }

            $(".cs-mystart-dropdown.user-options .cs-selector.guest").show();



            // ADD USER OPTIONS DROPDOWN TO THE DOM
            $(userOptionsItems).appendTo($(".cs-mystart-dropdown.user-options .cs-dropdown-list"));

            // BIND DROPDOWN EVENTS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });

        },

        "SchoolList": function() {
            // FOR SCOPE
            var template = this;
            var _this = this;

            if($(".sw-mystart-dropdown.schoollist").length) {
                var schools = { "Elementary": [], "Middle": [], "High": [], "EducationalAlternativeServices": [], "ExceptionalCenters": [], "CareerTechnicalAdultEducation": []};
                var schoolName = "", schoolUrl = "";
                var schoolColumns = 4;

                $(".sw-mystart-dropdown.schoollist .sw-dropdown-list > li").each(function() {
                    schoolName = $("> a", this).text();
                    schoolUrl = $("> a", this).attr("href");

                    // ELEMENTARY
                    if(schoolName.toLowerCase().indexOf("(es)") > -1) {
                        schools.Elementary.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["Elementary Schools"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                    // MIDDLE
                    if(schoolName.toLowerCase().indexOf("(ms)") > -1) {
                        schools.Middle.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["Middle Schools"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                    // HIGH
                    if(schoolName.toLowerCase().indexOf("(hs)") > -1) {
                        schools.High.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["High Schools"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                    // EDUCATIONAL ALTERNATIVE SERVICES
                    if(schoolName.toLowerCase().indexOf("(ea)") > -1) {
                        schools.EducationalAlternativeServices.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["Educational Alternative Services"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                    // EXCEPTIONAL CENTERS
                    if(schoolName.toLowerCase().indexOf("(ec)") > -1) {
                        schools.ExceptionalCenters.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["Exceptional Centers"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                    // CAREER TECHNICAL & ADULT EDUCATION
                    if(schoolName.toLowerCase().indexOf("(ct)") > -1) {
                        schools.CareerTechnicalAdultEducation.push({ "name": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl });
                        _this.RsMenuSchools["Career Technical & Adult Education"].push({ "text": $.trim(schoolName.replace(/\((.*?)\)/g, "")), "url": schoolUrl, "target": "_self" });
                    }

                });

                $("#gb-page").append('' +
                    '
' +
                        '
' +
                            '
' +
                                '
Elementary' +
                                '
Middle' +
                                '
High' +
                                '
Educational Alternative Services' +
                                '
Exceptional Centers' +
                                '
Career Technical & Adult Education' +
                            '' +
                            '
' +
                                '
' +
                                    '
' + buildSchoolLists("Elementary") + '' +
                                    '
' + buildSchoolLists("Middle") + '' +
                                    '
' + buildSchoolLists("High") + '' +
                                    '
' + buildSchoolLists("EducationalAlternativeServices") + '' +
                                    '
' + buildSchoolLists("ExceptionalCenters") + '' +
                                    '
' + buildSchoolLists("CareerTechnicalAdultEducation") + '' + 
                                '' +
                            '' +
                            '
' +
                                'CLOSE' +
                            '' +
                        '' +
                    '');


                    function buildSchoolLists(category) {
                        var schoolLists = "";

                        $.each(schools[category], function(index, school) {
                                    schoolLists += '
' + school.name + '';
                                 });

                        return schoolLists;
                    }


            }

            //FIX EC LIST
            $("#ec-schools-panel li:last-child").addClass("visually-hidden").clone().appendTo("#ec-schools-panel .school-list").removeClass("visually-hidden");

            //SCHOOL TAB NAV
            $(".school-tab").on('focus', function(){
                var tabNum = $(this).index();

                $(".school-tab").removeClass("active").attr({
                    "tabindex":"-1",
                    "aria-selected":"false"
                });
                $(this).addClass("active").attr({
                    "tabindex":"0",
                    "aria-selected":"true"
                });

                $(".school-listing-container").hide().removeClass("active").attr("aria-hidden","true");
                $(".school-listing-container").eq(tabNum).show().addClass("active").attr("aria-hidden","false");
            })

            $(".cs-mystart-button.schools").on("click keydown", function(e){
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    var schoolsButton = $(this);
                    if($(this).hasClass("open")){
                        closeSchools(schoolsButton);
                    } else {
                        openSchools(schoolsButton);
                    }
                }
            })

            $(".header-row.one .header-column.two").on("click", function(){
            	if($("body").attr("data-schools-open") == "true"){
                var usedToClose = $(this);
                	closeSchools(usedToClose);
                }

            });

            $("#close-schools").on("click keydown", function(e){
                if(_this.AllyClick(e)) {
                    e.preventDefault();
                    var usedToClose = $(this);
                    closeSchools(usedToClose);
                }
            })

            function openSchools(schoolsButton){
                var schoolsButton = schoolsButton
                $("#schools-overlay").fadeIn().addClass("open").attr("aria-hidden", "false");
                $(schoolsButton).addClass("open").attr("aria-expanded","true");
                $("body").attr("data-schools-open","true");
                $(".school-tab.active").focus();
            }

            function closeSchools(usedToClose){
            	var clickedElement = usedToClose;
                var schoolsButton = $(".cs-mystart-button.schools");
                $("#schools-overlay").removeClass("open").attr("aria-hidden", "true");
                $("body").attr("data-schools-open","false");
                setTimeout(function(){
                    $("#schools-overlay").fadeOut();
                    if($(usedToClose).attr("id") == "close-schools"){
                    	$(schoolsButton).focus().removeClass("open").attr("aria-hidden", "true");
                    } else {
                    	$(schoolsButton).removeClass("open").attr("aria-hidden", "true");
                    } 
                }, 1000)
            }

            //SCHOOL TABS
            $(".school-tab").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".school-tab:last-child").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).prev().focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".school-tab:first-child").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).next().focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            $(".ac-mystart-button.schools").focus();
                        } else {
                            e.preventDefault();

                            $(".school-listing-container.active li:first-child a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".school-tab:first-child").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".school-tab:last-child").focus();
                    break;

                    case template.KeyCodes.esc:
                        $("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                        $(this).attr("aria-expanded","false");
                        $(".ac-mystart-button.schools").removeClass("open");
                        setTimeout(function(){
                            $(".ac-mystart-button.schools").focus();
                        }, 100);
                    break;
                }
            });

            //SCHOOLS
            $("li.school a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        if($(this).parent().is(":first-child")){
                            $(".school-listing-container.active").find("li.school:last-child a").focus();
                        } else {
                        	if($(this).parent().prev().hasClass("visually-hidden")){
                            	$(this).parent().prev().prev().find("> a").focus();
                            } else {
                            	$(this).parent().prev().find("> a").focus();
                            }

                        }

                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            $(".school-tab.active").focus();
                        } else {
                            e.preventDefault();

                            $("#close-schools").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        if($(this).parent().is(":last-child")){
                            $(".school-listing-container.active").find("li.school:first-child a").focus();
                        } else {
                        	if($(this).parent().next().hasClass("visually-hidden")){
                            	$(this).parent().next().next().find("> a").focus();
                            } else {
                            	$(this).parent().next().find("> a").focus();
                            }

                        }

                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".school-listing-container.active").find("li.school:first-child a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".school-listing-container.active").find("li.school:last-child a").focus();
                    break;

                    case template.KeyCodes.esc:
                        $("#school-list-container").slideUp(300, "swing").attr("aria-hidden","true");
                        $(this).attr("aria-expanded","false");
                        $(".ac-mystart-button.schools").removeClass("open");
                        setTimeout(function(){
                            $(".ac-mystart-button.schools").focus();
                        }, 100);
                    break;
                }
            });

            $("#close-schools").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {


                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey){

                        } else {
                            e.preventDefault();
                            $(".cs-mystart-dropdown.translate .cs-selector").focus();
                            var usedToClose = "nothing";
                   			closeSchools(usedToClose);
                        }
                    break;
                }
            });

            $("#schools-overlay").on("keydown", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {


                    // CONSUME TAB KEY
                    case template.KeyCodes.esc:
                    	e.preventDefault();
                        var usedToClose = "nothing";
                   		closeSchools(usedToClose);
                    break;
                }
            });



        },

