    $(function() {
        CreativeTemplate.Init();
    });

    var CreativeTemplate = {
        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.MyStart();
            this.JsMediaQueries();
            this.ShortcutsFullHomePage();
            this.ShortcutsSubpage();
            this.SchoolList();
            this.Translate();
            this.Header();
            this.Search();
            this.RsMenu();
            this.ChannelBar();
            this.StickyChannelBar();
            this.StreamingVideo();
            this.Body();
            this.ModEvents();
            this.Homepage();
            this.GlobalIcons();
            this.AppAccordion();
            this.Footer();
            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
            if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
        	this.MegaMenu();
            this.HomepageHeadlineSliders();

        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.ShortcutsFullHomePage();
            this.ShortcutsSubpage();
        },

        "WindowScroll": function() {
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    //MOVE PASSKEYS
                    if(!$("#gb-mystart #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo("#gb-mystart");
                    }
                break;
                case "768":
                    //MOVE PASSKEYS
                    if(!$("#nv-right #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo("#nav-right");
                    }
                break;
                case "640":
                    //MOVE PASSKEYS
                    if(!$("#nv-right #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo("#nav-right");
                    }
                break;
                case "480":
                    //MOVE PASSKEYS
                    if(!$("#nv-right #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo("#nav-right");
                    }
                break;
                case "320":
                    //MOVE PASSKEYS
                    if(!$("#nv-right #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo("#nav-right");
                    }
                break;
            }
        },

        "MyStart": function() {
            // FOR SCOPE
            var _this = this;

            // BUILD USER OPTIONS DROPDOWN
            var userOptionsItems = "";



            // SIGNIN BUTTON
            if($(".sw-mystart-button.signin").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.signin").html() + "";
            }

            // REGISTER BUTTON
            if($(".sw-mystart-button.register").length) {
                userOptionsItems += "
" + $(".sw-mystart-button.register").html() + "";
            }



            // ADD USER OPTIONS DROPDOWN TO THE DOM
            $(".cs-mystart-dropdown.user-options .cs-dropdown-list").html(userOptionsItems);

        	// ADD SCHOOLS
            if(!$(".preview-changes #cs-schools-list").hasClass("loaded")) {
                // AJAX IN THE SCHOOL LIST
                // CHECK IF IT IS SUPPORTED BY THE USERS BROWSER
                if(typeof(Storage) !== "undefined"){ // IS SUPPORTED
                    if(sessionStorage.getItem("fairbanksSchoolList") === null) {
                        // ITEM DOES NOT EXIST IN STORAGE
                        $("#cs-schools-list").load("/cms/lib/GA01000373/Centricity/template/38/html/forsyth-school-menu.html", function(){

                            // STORE SCHOOL LIST
                            // JSON STRINGIFY IS USED BECAUSE SOME JAVASCRIPT MAY BE STORED
                            var stuffToStore = $("#cs-schools-list").html();
                            sessionStorage.setItem("forsythSchoolList", JSON.stringify(stuffToStore));
                            $("#cs-schools-list").addClass("loaded");

                            // BIND THE MENU EVENTS
                            //_this.SchoolMenuControls();
                        });
                    } else {
                        // ITEM DOES EXIST IN STORAGE
                        // JSON PARSE IS USED BECAUSE OF THE CONTAINED JAVASCRIPT
                        $("#cs-schools-list").html(JSON.parse(sessionStorage.getItem("forsythSchoolList")));

                        $("#cs-schools-list").addClass("loaded");

                        // BIND THE MENU EVENTS
                        //_this.SchoolMenuControls();
                    }
                } else { // IS NOT SUPPORTED
                    // BROWSER DOES NOT SUPPORT HTML5 STORAGE
                    $("#cs-schools-list").load("/cms/lib/GA01000373/Centricity/template/38/html/forsyth-school-menu.html", function(){
                        $("#cs-schools-list").addClass("loaded");

                        //_this.SchoolMenuControls();
                    });
                }
            }

            // BIND DROPDOWN EVENTS
            this.DropdownActions({
                "dropdownParent": ".cs-mystart-dropdown.user-options",
                "dropdownSelector": ".cs-selector",
                "dropdown": ".cs-dropdown",
                "dropdownList": ".cs-dropdown-list"
            });
        },

        "SchoolList": function() {
            // ADD SCHOOL LIST
            if(this.ShowSchoolList) {
            	var schoolDropDownListItems = '';
                $(".sw-channel-item").each(function(){
                	if($(this).find("> a span").text() == "Schools"){
                    	schoolDropDownListItems = $(this).find(".sw-channel-dropdown").html();
                    }
                })

                var schoolDropdown =    '
' +
                                            '
Our Schools' +
                                            '
' +
                                                '' + schoolDropDownListItems + '' +
                                            '' +
                                        '';

                // ADD SCHOOL LIST TO THE DOM   
                $(".cs-mystart-button.home").after(schoolDropdown);

                // BIND DROPDOWN EVENTS
                this.DropdownActions({
                    "dropdownParent": ".cs-mystart-dropdown.schools",
                    "dropdownSelector": ".cs-selector",
                    "dropdown": ".cs-dropdown",
                    "dropdownList": ".cs-dropdown-list"
                });
            }
        },

        "Translate": function() {
            // ADD TRANSLATE
            if(this.ShowTranslate) {
                $(".cs-mystart-dropdown.translate .cs-dropdown").creativeTranslate({
                    "type": 2, // 1 = FRAMESET, 2 = BRANDED, 3 = API
                    "languages": [ // ["ENGLISH LANGUAGE NAME", "TRANSLATED LANGUAGE NAME", "LANGUAGE CODE"]
                        ["Afrikaans", "Afrikaans", "af"],
                        ["Albanian", "shqiptar", "sq"],
                        ["Amharic", "", "am"],
                        ["Arabic", "", "ar"],
                        ["Armenian", "", "hy"],
                        ["Azerbaijani", "Azrbaycan", "az"],
                        ["Basque", "Euskal", "eu"],
                        ["Belarusian", "", "be"],
                        ["Bengali", "", "bn"],
                        ["Bosnian", "bosanski", "bs"],
                        ["Bulgarian", "", "bg"],
                        ["Burmese", "", "my"],
                        ["Catalan", "catal", "ca"],
