                        	var index = $(".mega-channel a").index(this)-1;
                        	$(".mega-channel a").eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(".mega-channel a").index(this) == $(".mega-channel a").length) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(".mega-channel a").index(this)+1;
                            $(".mega-channel a").eq(index).focus();
                        }
                    break;
                    case _this.KeyCodes.tab:
                   		e.preventDefault();
                        var index = $(".mega-channel a").index(this)+1;
                        $(".mega-channel a").eq(index).focus();
                    break;
                  }
            });
        }, 

        "Body": function() {
            // FOR SCOPE
            var _this = this;

            //ADD CLASS FOR UNBUPLISHED CHANGES
            if($(".sw-special-mode-bar").length || $('#templateconfiguration-iframe-preview', window.parent.document).length) {
            	$("#gb-page").addClass("preview-changes");
            }

            //REMOVE UNUSED LINKS
            $("[data-toggle='false']").remove();

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // CHECK PAGELIST HEADER
            if($.trim($(".ui-widget.app.pagenavigation .ui-widget-header").text()) == "") {
                $(".ui-widget.app.pagenavigation .ui-widget-header").html("
");
            }

            //SUBPAGE BACKGROUND IMAGE
            var subpageBG = jQuery.trim("/cms/lib/GA01000373/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var subpagesrcSplit = subpageBG.split("/");
            var subpagesrcSplitLen = subpagesrcSplit.length;
            if((subpageBG == "") || (subpagesrcSplit[subpagesrcSplitLen - 1] == "default-man.jpg")) {
                $("#sp-content-outer").css("background-image","url(/cms/lib/GA01000373/Centricity/Template/38/subpage-default-bg.jpg)");
            } else {
                $("#sp-content-outer").css("background-image","url("+subpageBG+")");
            }

            //HOVERS ON SHORTCUTS
            $(".navigation li a").hover(function() {
                $(this).parent().addClass("hover");
            }, function() {
                $(this).parent().removeClass("hover");
            });      

        },

        "HomepageHeadlineSliders": function(){
