            })
        },

        "Homepage": function() {
            // FOR SCOPE
            var _this = this;

            //SHOW EF REGIONS IF APPS EXIST
            if($(".hp-row.five .app").length > 0){
            	$(".hp-row.five").show();
            }

            //ARTICLE SLIDERS
            $(".hp-row:not(.five) .headlines").csArticleSlider({
                "viewerNum": [4, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5, // SECONDS
                "slideFullView": true
            });

            //MOVE SLIDER BUTTONS AND MORE LINKS
            $(".hp-row:not(.five) .headlines").each(function(){
                var backButton = $(this).find(".cs-article-slider-control.back")[0].outerHTML;
                var nextButton = $(this).find(".cs-article-slider-control.next")[0].outerHTML;
                var moreLink = $(this).find(".ui-read-more").html();

                if($(this).find(".ui-read-more").length){
                	 $(this).find(".ui-read-more").remove();
                	$("
"+backButton+nextButton+moreLink+"").prependTo($(this).find(".ui-widget-detail"));
                } else {
                	$("
"+backButton+nextButton+"").prependTo($(this).find(".ui-widget-detail"));
                }
            })

            //REGIONS B & F HEADLINES
           $(".hp-row:not(.five) .headlines li").each(function(){
                //WRAP HEADLINE TEXT
                $(this).find(".ui-article-description").text($.trim($(this).find(".ui-article-description").text()));
                $(this).find(".ui-article-title, .ui-article-description, .ui-article-controls").wrapAll("

");
                $(this).find(".ui-article-description").wrap("

");
                $(this).find(".ui-article-description").attr("aria-hidden","true");
            })

            $(".hp-row:not(.five) .upcomingevents").csArticleSlider({
                "viewerNum": [4, 2, 2, 1, 1], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "transitionSpeed": .5 // SECONDS
            });

            //HOVERS
            $(".hp-row:not(.five) .headlines li").hover(function() {
            	if($(".ui-article-description", this).text() != "") {
                	$(this).addClass("hover");
                	$(this).find(".ui-article-description").attr("aria-hidden","false");                
                }
            }, function() {
                if($(".ui-article-description", this).text() != "") {
                	$(this).removeClass("hover");
                	$(this).find(".ui-article-description").attr("aria-hidden","true");              
                }
            });

            //FOCUS
            $(".hp-row:not(.five) .headlines li .ui-article-title a").focus(function() {
            	if($(this).closest("li").find(".ui-article-description").text() != "") {
                	$(this).closest("li").addClass("hover");
                	$(this).closest("li").find(".ui-article-description").attr("aria-hidden","false");
                }
            });

            $(".hp-row:not(.five) .headlines li .ui-article-title a").focusout(function() {
            	if($(this).closest("li").find(".ui-article-description").text() != "") {
                	$(this).closest("li").removeClass("hover");
                	$(this).closest("li").find(".ui-article-description").attr("aria-hidden","true");
                }
            });

            //MOVE SLIDER BUTTONS AND MORE LINKS
            $(".hp-row:not(.five) .upcomingevents").each(function(){
                var backButton = $(this).find(".cs-article-slider-control.back")[0].outerHTML;
                var nextButton = $(this).find(".cs-article-slider-control.next")[0].outerHTML;
                var calendarLink = $(this).find(".view-calendar-link")[0].outerHTML;

                $("
"+backButton+nextButton+calendarLink+"").prependTo($(this).find(".ui-widget-detail"));
            })

            //REGION C BACKGROUND IMAGE
            var regionCBG = jQuery.trim("/cms/lib/GA01000373/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var regionCsrcSplit = regionCBG.split("/");
            var regionCsrcSplitLen = regionCsrcSplit.length;
            if((regionCBG == "") || (regionCsrcSplit[regionCsrcSplitLen - 1] == "default-man.jpg")) {
                $(".hp-row.three").css("background-image","url(/cms/lib/GA01000373/Centricity/Template/38/region-c-default-bg.jpg)");
            } else {
                $(".hp-row.three").css("background-image","url("+regionCBG+")");
            }

            //REGION D BACKGROUND IMAGE
            var regionDBG = jQuery.trim("/cms/lib/GA01000373/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            var regionDsrcSplit = regionDBG.split("/");
            var regionDsrcSplitLen = regionDsrcSplit.length;
            if((regionDBG == "") || (regionDsrcSplit[regionDsrcSplitLen - 1] == "default-man.jpg")) {
                $(".hp-row.four").css("background-image","url(/cms/lib/GA01000373/Centricity/Template/38/region-d-default-bg.jpg)");
            } else {
                $(".hp-row.four").css("background-image","url("+regionDBG+")");
            }
        },

        "ShortcutsFullHomePage": function() {
            var _this = this;

            // EDIT THESE TWO VARS
            var columnNums = [5, 3, 2, 1, 1]; // [960, 768, 640, 480, 320]
            var selector = "#sw-content-container1.ui-hp .siteshortcuts, #sw-content-container2.ui-hp .siteshortcuts, #sw-content-container3.ui-hp .siteshortcuts, #sw-content-container4.ui-hp .siteshortcuts";

            // RETURN BREAKPOINT INDEX
            var bp = function() {
                switch(_this.GetBreakPoint()) {
                    case "desktop": return 0; break;
                    case "768": return 1; break;
                    case "640": return 2; break;
                    case "480": return 3; break;
                    case "320": return 4; break;
                }
            }

            // SET COLUMN NUM AND OTHER VARS
            var columnNum = columnNums[bp()];
            var endRange;

            $(selector).each(function() {
                // RETURN THE LI'S TO THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).append($(".site-shortcuts-column > li", this));

                // REMOVE COLUMN CONTAINER FOR REBUILD
                $(".site-shortcuts-columns", this).remove();

                // GET SHORTCUT NUM
                var shortcutNum = $(".ui-widget-detail > ul.site-shortcuts > li", this).length;

                // ADD COLUMN CONTAINER
                $(".ui-widget-detail", this).prepend('
');

                // LOOP TO BUILD COLUMNS
                for(var i = 0; i < columnNum; i++) {
                    // KEEP FROM ADDING EMPTY UL'S TO THE DOM
                    if(i < shortcutNum) {
                        // IF shortcutNum / columnNum REMAINDER IS BETWEEN .0 AND .5 AND THIS IS THE FIRST LOOP ITERATION
                        // WE'LL ADD 1 TO THE END RANGE SO THAT THE EXTRA LINK GOES INTO THE FIRST COLUMN

                        if((shortcutNum / columnNum) % 1 > 0.0 && (shortcutNum / columnNum) % 1 < 0.5 && i == 0) {
                           endRange = Math.round(shortcutNum / columnNum) + 1;
                       } else if((shortcutNum / columnNum) % 1 == 0.5 && i >= (columnNum / i)) {
                           endRange = Math.round(shortcutNum / columnNum) - 1;
                       } else {
                           endRange = Math.round(shortcutNum / columnNum)
                       }

                        // ADD THE COLUMN UL
                        $(".site-shortcuts-columns", this).append('');

                        // MOVE THE RANGE OF LI'S TO THE COLUMN UL
                        $(".site-shortcuts-column.column-" + (i + 1), this).append($(".ui-widget-detail > ul.site-shortcuts > li:nth-child(n+1):nth-child(-n+" + endRange +")", this));
                    }
                }

                // HIDE THE ORIGINAL UL
                $(".ui-widget-detail > ul.site-shortcuts", this).hide();
            });
        },

        "ShortcutsSubpage": function() {
