        "Footer": function() {
            // FOR SCOPE
            var _this = this;

            //REMOVE UNUSED SOCIAL ICONS
            var socialIconToggles = [true, true, true, true, true, false, false, false, false, false, false];

            for(i = 0; i < socialIconToggles.length; i++){
                if(socialIconToggles[i] == true){
                    $(".footer-row.one").show();
                }
            }

            $(".social-icon[data-toggle='false'], .footer-link[data-toggle='false']").remove();

            //SHOW DISCLAIMER
            var disclaimer = jQuery.trim("");

            if(disclaimer != ""){
            	$(".footer-row.three").show();
            }


        },

        "StreamingVideo": function() {
            // FOR SCOPE
            var _this = this;

                var videoVendor = "YouTube";
                //var mobilePhoto = "/cms/lib/GA01000373/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg";
                var mobilePhoto = "";

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                    mobilePhoto = "/cms/lib/GA01000373/Centricity/Template/38/default-mobile-video-image.jpg";
                }

                $("#video").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "VLuxHRuBZ_A", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [960], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showWatchVideoButton" : true,
                    "watchVideoButtonText" : "Watch Background Video",
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : true,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 640, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function() {

                    	//CHECK FOR TITLE, DESCRIPTION, OR LINK
                        if(false == true || false == true || false == true){
                            $("#hp-video-text-inner").removeClass("no-text");
                        }

                        //MOVE BUTTONS
                        $(".cs-fullscreen-video-button.pause-button, .cs-fullscreen-video-button.play-button").insertBefore(".cs-fullscreen-video-button.mute-button");
                        $("").appendTo(".cs-fullscreen-video-buttons");;
                        $(".cs-fullscreen-video-buttons").prependTo("#hp-video-text").css("visibility","visible");

                        $("#hp-video-content").appendTo("#video").show();

                        //SCROLL DOWN
                        $(".scroll-down").on('click keypress', function(e){
                                if(_this.AllyClick(e) === true){
                                //DONT LET THE PAGE JUMP ON KEYPRESS
                                event.preventDefault();
                                    $('html, body').animate({
                                        scrollTop: $(this).offset().top + 150
                                }, 500);
                            }
                        });

                        //VIDEO BUTTON INTERACTION
                        $("#hp-video .cs-fullscreen-video-button.mute-button").attr("aria-label", "Mute Background Video");
                        $("#hp-video .cs-fullscreen-video-button.unmute-button").attr("aria-label", "Unmute Background Video");
                        $("#hp-video .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#hp-video .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");
                        $('#hp-video .cs-fullscreen-video-button.watch-video-button').attr("aria-label", "Watch Background Video").attr('id', 'streaming-video-popout');

                        $("#hp-video").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#hp-video .cs-fullscreen-video-button.pause-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-video").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#hp-video .cs-fullscreen-video-button.play-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-video").on("click keydown", ".cs-fullscreen-video-button.mute-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#hp-video .cs-fullscreen-video-button.unmute-button").focus();
                                }, 100);
                            }
                        });
                        $("#hp-video").on("click keydown", ".cs-fullscreen-video-button.unmute-button", function(e) {
                            if(_this.AllyClick(e)) {
                                setTimeout(function () {
                                    $("#hp-video .cs-fullscreen-video-button.mute-button").focus();
                                }, 100);
                            }
                        });

                        $('#streaming-video-popout').on('click keydown', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
                                $('body').addClass('video-popup-showing');
                            }
                        });

                        $('body').on('click keydown', '.cs-fullscreen-video-close', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
	                            $('body').removeClass('video-popup-showing');
							}
                        });

                         switch(_this.GetBreakPoint()) {
                			case "desktop": 
                            	if($(".ios").length){
                                	$(".cs-fullscreen-video-button.pause-button").click();
                                }

                            break;
                         }

                    },
                    "onStateChange" : function() { }
                });
        },

        "ModEvents": function() {
            // FOR SCOPE
            var template = this;

            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "yes",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).appendTo($(this));
                    });

                    $(".joel-day", this).prependTo($(this).find(".upcoming-column.right"));
                    var dateMonth = $(".joel-month", this).text();
                    $(".joel-month", this).text(dateMonth+" ");

                }) 
            }

            //ADD NO EVENTS TEXT
            $(".upcomingevents").each(function(){
                if(!$(this).find(".ui-article").length){
                    $("

There are no upcoming events to display.").appendTo($(this).find(".ui-articles"));
                }
            });

        },

