        "Search": function() {
            // FOR SCOPE
            var _this = this;

            //OPENING SEARCH
            $("#gb-search-toggle").on('click keypress', function(event){
                var searchControl = this;

                if(_this.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if(!$(this).hasClass("open")){
                        $(searchControl).addClass("open").attr("aria-expanded","true");
                        $("#gb-search").addClass("open");
                        $("#gb-search-input").attr("tabindex","0").focus();
                        $("#gb-search-submit").attr("tabindex","0");
                    } else {
                        $(searchControl).removeClass("open").attr("aria-expanded","false");
                        $("#gb-search").removeClass("open");
                        $("#gb-search-input").attr("tabindex","-1");
                        $("#gb-search-submit").attr("tabindex","-1");
                    }
                }
            })

            //SEARCH FORM SUBMISSION
            $("#gb-search-form").submit(function(e){
                e.preventDefault();

                if($.trim($("#gb-search-input").val()) != "I'm looking for..." && $.trim($("#gb-search-input").val()) != "") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=1&SearchString=" + $("#gb-search-input").val();
                }
            });

            //SEARCH INPUT TEXT
            $("#gb-search-input").focus(function() {
                if($(this).val() == "I'm looking for...") {
                    $(this).val("");
                }
            });

            $("#gb-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("I'm looking for...");
                }
            });

            //SEARCH KEYBOARD NAV
            $("#gb-search-input").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                            //e.preventDefault(); 
                            //$("#gb-search-control").focus();
                        } else {
                        	e.preventDefault(); 
                        	$("#gb-search-toggle").focus();
                        }
                    break;
                }
            });

            $("#gb-search-toggle").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                            //e.preventDefault(); 
                            //$("#gb-search-control").focus();
                        } else {

                        	if($(this).hasClass("open")){
                            	e.preventDefault(); 
                            	$("#gb-search-submit").focus();
                            }
                        }
                    break;
                }
            });

            $("#gb-search-submit").on("keydown", function(e){
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    case _this.KeyCodes.tab:
                        if(!e.shiftKey){
                            e.preventDefault();
                            $("#gb-search-toggle").focus();
                        }
                    break;
                }
            });

            $("#gb-logo a").focus(function() {
                if($("#gb-search-control").hasClass("open")) {
                    $("#gb-search-control").removeClass("open").attr("aria-expanded","false");
                    $("#gb-search").removeClass("open").attr("aria-hidden","true");
                    $("#gb-search-input").attr("tabindex","-1");
                }
            });
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

