div.script:
var mediaModalTimeout;

function showMediaModal(id,url) {
    window.clearInterval(mediaModalTimeout);
    $(id).modal('show').find('iframe').attr('src',url);
    window.setTimeout(function() {
        mediaModalTimeout = window.setInterval(function() {
            $('.modal.darkbox:not(.in) iframe[src]').removeAttr('src');
        },500);
    },1000);
    return false;
}

$(document).ready(function() {
	$('#newswindow-6 article').first().addClass('current');
	slideshowTimer6 = window.setInterval(nextSlide6,slideshowDelay);
	$('#newswindow-6').hover(function() {
		window.clearInterval(slideshowTimer6);
		fadeSpeed = fadeSpeedHover;
	},function() {
		slideshowTimer6 = window.setInterval(nextSlide6,slideshowDelay);
		fadeSpeed = fadeSpeedNormal;
	}).bind('keydown', function(e) {
		if (e.keyCode==13) {
			window.clearInterval(slideshowTimer6);
			fadeSpeed = fadeSpeedHover;
		}
	});
	$('html').bind('keydown', function(e) {
		if (e.keyCode==9) {
			window.clearInterval(slideshowTimer6);
			fadeSpeed = fadeSpeedHover;
		}
	});
});

var slideshowDelay = 5000;
var fadeSpeedNormal = 1000;
var fadeSpeedHover = 250;
var fadeSpeed = fadeSpeedNormal;
var slideshowTimer6;

function nextSlide6() {
}

function prevSlide6() {
}

function googleTranslateElementInit() {
	new google.translate.TranslateElement({
		pageLanguage: "en",
		layout: google.translate.TranslateElement.InlineLayout.HORIZONTAL,
		gaTrack: true, gaId: 'UA-7046710-1'
	}, "google_translate_element");
}

