$(document).ready(function() {

	$('.collapse').collapse({
		toggle: false
	});

	$(".tagcal-month .tagcal-month").tooltip({placement: "bottom", delay: {show: 250, hide: 0}});
});

// Prevent messy empty anchors & allow for hover on nav dropdown
$(document).ready(function() {
	$('.mps-navbar a[href="#"]').click(function(e) {
		e.preventDefault();
	});

	var isTouch = 'ontouchstart' in window || navigator.MaxTouchPoints > 0 || navigator.msMaxTouchPoints > 0;

	if (!isTouch) {
		$(".dropdown").hover(
			function() {
				$('.dropdown-menu', this).fadeIn("fast");
			},
			function() {
				$('.dropdown-menu', this).fadeOut("fast");
			}
		);
	}
});

// Footer translate DHTML function
function openGoogleTranslate() {
	$('.footer-nav').hide();
	$('.footer-translate').show();
	$('.goog-te-gadget .goog-te-combo').change(function() {
		$('.footer-nav').show();
		$('.footer-translate').hide();
	});
}
