div.script:
        (function () {
            function createCookie(name, value, days) {
                if (days) {
                    var date = new Date();
                    date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
                    var expires = "; expires=" + date.toGMTString();
                }
                else var expires = "";
                document.cookie = name + "=" + value + expires + "; path=/";
            }

            function readCookie(name) {
                var nameEQ = name + "=";
                var ca = document.cookie.split(';');
                for (var i = 0; i < ca.length; i++) {
                    var c = ca[i];
                    while (c.charAt(0) == ' ') c = c.substring(1, c.length);
                    if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
                }
                return null;
            }

            function eraseCookie(name) {
                createCookie(name, "", -1);
            }

            var playPauseClickFxn = (function (a) {
                return function (e) {
                    if (a.vid.paused) {
                        a.plBtn.setAttribute("aria-hidden", true);
                        a.paBtn.setAttribute("aria-hidden", false);
                        a.vid.play();
                        //set cookie to play
                        var cook = readCookie("videoPausedByUser");
                        if (cook !== "false") {
                            if (cook !== null) { eraseCookie("videoPausedByUser"); }
                            createCookie("videoPausedByUser", "false");
                        }
                    } else {
                        a.plBtn.setAttribute("aria-hidden", false);
                        a.paBtn.setAttribute("aria-hidden", true);
                        a.vid.pause();
                        //set cookie to pause
                        var cook = readCookie("videoPausedByUser");
                        if (cook !== "true") {
                            if (cook !== null) { eraseCookie("videoPausedByUser"); }
                            createCookie("videoPausedByUser", "true");
                        }
                    }
                }
            }({
                vid: document.querySelector('video'),
                paBtn: document.querySelector(".v-btn.v-pause"),
                plBtn: document.querySelector(".v-btn.v-play")
            }));
            document.querySelector('.v-btn.v-pause').addEventListener('click', playPauseClickFxn);
            document.querySelector('.v-btn.v-play').addEventListener('click', playPauseClickFxn);
            var captionsFxn = (function (a) {
                return function (e) {
                    if (a.trackEl.track.mode === "disabled") {
                        a.trackEl.track.mode = "showing";
                    } else {
                        a.trackEl.track.mode = "disabled";
                    }
                }
            }({
                trackEl: document.querySelector('video > track')
            }));
            document.querySelector('.v-btn.v-cc').addEventListener('click', captionsFxn);
        }());

