        window.resizeMiniCalendar20200419013832661 = function () {
        var calId = 'miniCal20200419013832661';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200419013832661';
        var calMonthFullId = 'miniCalMonthFull20200419013832661';
        var calMonthShortId = 'miniCalMonthShort20200419013832661';
        var calGoToCalFullId = 'miniCalGotToCalFull20200419013832661';
        var calGoToCalShortId = 'miniCalGoToCalShort20200419013832661';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

