        "Header": function() {
            //ADD LOGO
            var logoCheck = "true";
            var logoSrc = jQuery.trim("/cms/lib/IL01904775/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg");
            if(logoCheck == "true") {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
                    $("#gb-logo").append('').removeClass("hidden");
                } else {
                    $("#gb-logo").append('').removeClass("hidden");
                }
            }

            //ADD SITENAME
            var siteNameOne = jQuery.trim('LaSalle-Peru Township');
            var siteNameTwo = jQuery.trim('High School District 120');
            var siteNameThree = jQuery.trim('');
            if((siteNameOne == "") && (siteNameTwo == "")) {
                var splitLen = 2;
                var siteName = "LaSalle-Peru Township High School District 120";
                siteName = siteName.split(" ");
                var siteNameLength = siteName.length;
                if(siteNameLength > 2){
                    siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
                } else {
                    siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
                }
                siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
                $("#gb-sitename").prepend("
" + siteNameBegin + "" + siteNameEnd + "");
            } else if((siteNameOne != "") && (siteNameTwo == "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "");
            } else if((siteNameOne == "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameTwo + "");
            } else if((siteNameOne != "") && (siteNameTwo != "")) {
                $("#gb-sitename").prepend("
" + siteNameOne + "" + siteNameTwo + "");
            }
            if((siteNameThree != "")){
            	$(".hp #gb-sitename").append("" + siteNameThree + "");
            }
        },
        "ChannelBar": function() {
            //USE CHANNEL NAME FOR PAGE LIST NAVIGATION HEADER IF ONE NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }

            //ADD FIRST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").first().addClass("first");
            //ADD LAST CLASS TO LAST CHANNEL ITEM
            $("li.sw-channel-item").last().addClass("last");

            $("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){

                // LOWERCASE THE CHANNEL NAME
                var channelText = $("> a span", this).text().toLowerCase();
                // SET HOME AND CALENDAR CLASSES ON HOME AND CALENDAR CHANNELS
                if(channelText == "home") {
                    $(this).addClass("home");
                } else if(channelText == "calendar") {
                    $(this).addClass("calendar");
                }

                var windowWidth = $(window).width();
                var offset = $(this).offset();
                var offsetLeft = offset.left;
                console.log(offsetLeft);
                if((offsetLeft + 634) > windowWidth){
                	$("ul.sw-channel-dropdown",this).css({
                			"left": "auto",
                            "right": "0px"
                    });
                }

            });

            $(window).resize(function(){
            	$("#gb-channel-list #sw-channel-list-container li.sw-channel-item").each(function(index){
                	var windowWidth = $(window).width();
                    var offset = $(this).offset();
                    var offsetLeft = offset.left;
                    console.log(offsetLeft);
                    if((offsetLeft + 634) > windowWidth){
                        $("ul.sw-channel-dropdown",this).css({
                                "left": "auto",
                                "right": "0px"
                        });
                    }
                });
            });

            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            //ADD TRANSITION CLASS TO ALL CHANNEL ITEMS
            $("ul.sw-channel-list li.sw-channel-item").addClass("transition");
        },

        "StickyChannelBar": function() {
        	if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = $("nav").offset().top;
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                                $(".wcm-controls").css("margin-top", "0px");
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            	if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                            }
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("nav").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }

                        if($(".sp").length && $(".ui-widget.app.calendar").length) {
                            $(".wcm-controls").css("margin-top", "0px");
                        }
                        $("#sw-maincontent").removeAttr("style");
                    break;
                }
            }


        },

        "StickyVideoButton": function() {
        	var scrollTop = $(document).scrollTop();
            var videoHeight = $("#cs-fullscreen-video-outer").height();
            var windowHeight = $(window).height();
            var videoDiff = (videoHeight - windowHeight) + 105;
            if(!$(".lt-1024").length){
            	//$(".sp main").css("margin-top", contentOffset);
                if(scrollTop >= videoDiff){
                    $("#hp-slideshow").addClass("unfixed");
                } else {
                    $("#hp-slideshow").removeClass("unfixed");
                }
            }
        },

