                            var srcSplit = imgSrc.split("/");
                            var srcSplitLen = srcSplit.length;
                            if(imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg" && imgCheck != "false") {
                                imgColumn = '
