                    if($("img[src]", this).length) {
                        var imgSrc = $("img", this).attr("src");
                        $(this).addClass("has-thumb");
                        $(".ui-article", this).children().not($(".ui-article-thumb", this)).wrapAll('

');
                        $(".cs-headline-detail", this).children().not($(".ui-article-title", this)).wrapAll('

');
