                });
            });

            // ADD/REMOVE HOVER CLASS FOR HOVER/FOCUS/TOUCH
			$(document).on("mouseenter focusin touchstart", ".ui-widget.app.headlines .ui-articles > li .ui-article", function(e) {
            	$(this).addClass("hover");
